import React, { Component } from "react";
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form'
import Alert from 'react-bootstrap/Alert';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';

const backendUrl = 'http://localhost:8001';

class LatestScores extends Component
{


  constructor(props) {
    super(props);

    this.state = {
      status: 'OK', textArea: "Tu powinna być zawartość bazy danych"         
    };
            
  }

  async getScores() {
    
    await axios.get(`${backendUrl}/api/v1/getScores`)
      .then(response => {
        
        let scoresListText = ""

        response.data.forEach((e) => {
            let lid = e.id; 
            let lusername = e.username; let lhiragana_score_1 = e.hiragana_score_1; let lhiragana_score_2 = e.hiragana_score_2; let lhiragana_score_3 = e.hiragana_score_3; let lkatakana_score_1 = e.katakana_score_1; let lkatakana_score_2 = e.katakana_score_2; let lkatakana_score_3 = e.katakana_score_3;

            scoresListText = scoresListText + lid + ", " + lusername + ", " + lhiragana_score_1 + ", " + lhiragana_score_2 + ", " + lhiragana_score_3 + ", " + lkatakana_score_1 + ", " + lkatakana_score_2 + ", " + lkatakana_score_3 + "\n";
            this.setState({ textArea: scoresListText, status: "OK" });

        //   let firstPartText = lusername.substring(0, ("ERROR:").length);


        //   if (firstPartText !== "ERROR:") {
        //     scoresListText = scoresListText + lid + ", " + lusername + ", " + lhiragana_score_1 + ", " + lhiragana_score_2 + ", " + lhiragana_score_3 + ", " + lkatakana_score_1 + ", " + lkatakana_score_2 + ", " + lkatakana_score_3 + "\n";
        //     this.setState({ textArea: scoresListText, status: "OK" });
        //   }
        //   else {
        //     let secondPartText = lusername.substring(("ERROR:").length, lusername.length);
        //     scoresListText = lusername + "\n";
        //     this.setState({ textArea: scoresListText, status: secondPartText });
        //   }
        })

      }).catch(err => {

        let myerror = "Błąd połaczenia sieciowego." + err;
        this.setState({ status: myerror });
      });

    }

    componentDidMount() {
        this.getScores();
      }

  render() {
    return (

      <Container>

        <Alert variant="info">
          <Alert.Heading><strong>Klient dostępu do przelewów w banku z użyciem REST (bez autoryzacji, CLASS) </strong></Alert.Heading>
          Lokalizacja bazy: http://localhost:8081/
        </Alert>

        <p></p>

        <h3>Status: {this.state.status}</h3>

        <p></p>

        <Form.Control as="textarea" rows={8} type="text" value={this.state.textArea} spellCheck="false" />

        </Container>

    );
  }

}

export default LatestScores;
