import React, { Component, FormEvent, ChangeEvent } from "react";

import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';


interface Props {
};

interface State {
    name: string;
    age: string;
    gender: string;

    isErrorName: boolean;
    errorNameText: string;
}


class ManyElemFormValid extends Component<Props, State>
{
    constructor(props: Props) {
        super(props);
        this.state = {
            name: "a",
            age: "0",
            gender: "N",

            isErrorName: false,
            errorNameText: "OK"
        }

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }


    handleChange(evt: ChangeEvent<HTMLInputElement>) {

        const name = evt.target.name;

        if (name == "name") {

            this.setState({ name: evt.target.value });
        }
        else
            if (name == "age") {
                this.setState({ age: evt.target.value });
            }
            else {
                this.setState({ gender: evt.target.value });
            }
    }


    nameValidation(name: string): string {        
        if ((name.length < 2) || (name.length > 20)) {            
            return "Za krótkie lub za długie imie";
        }
        else {
            return "OK";
        }

    }

    handleSubmit(event: FormEvent<HTMLFormElement>) {
        
        let resultForName = this.nameValidation(this.state.name);
        if (resultForName !== "OK") {
                                    
            this.setState((prevState) => ({
                isErrorName: true, errorNameText: resultForName                
            }));            
        }
        else {            
            this.setState({ isErrorName: false, errorNameText: "OK" });            
        }

        event.preventDefault();

    }

    render() {

        return (

            <Form onSubmit={this.handleSubmit}>

                <h3>Wynik wprowadzania danych: {this.state.name}, {this.state.age}, {this.state.gender}, {this.state.isErrorName}, {this.state.errorNameText}</h3>

                <Form.Group className="auto">
                    <Form.Label>Podaj imię:</Form.Label>
                    <Form.Control type="text" name='name' value={this.state.name} placeholder="" onChange={this.handleChange} />
                    {this.state.isErrorName && (<Form.Label>{this.state.errorNameText}</Form.Label>)}                    
                </Form.Group>

                <div><p></p></div>

                <Form.Group className="auto">
                    <Form.Label>Podaj wiek:</Form.Label>
                    <Form.Control type="text" name='age' value={this.state.age} placeholder="" onChange={this.handleChange} />
                </Form.Group>

                <div><p></p></div>

                <Form.Group className="auto">
                    <Form.Label>Podaj płeć:</Form.Label>
                    <Form.Control type="text" name='city' value={this.state.gender} placeholder="" onChange={this.handleChange} />
                </Form.Group>

                <div><p></p></div>

                <Button variant="primary" type="submit">  Potwierdź  </Button>
            </Form >

        );
    }
}

export default ManyElemFormValid;
