import React, { Component, FormEvent, ChangeEvent } from "react";

import  SPerson from "./SPerson"

interface Props {    
    callback : (p: SPerson) => void;        
};

interface State {
    email: string;
    firstName: string;
    lastName: string;
    age: number;    
}


class ReadPerson extends Component<Props, State>
{
    constructor(props: Props) {
        super(props);
        this.state = { email: 'e', firstName: 'f', lastName: 'l', age: 22};
        
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }


    handleChange(e: ChangeEvent<HTMLInputElement>): void {

        const name = e.target.name;
        if (name == "email") {
            this.setState({ email: e.target.value, firstName: this.state.firstName, 
                            lastName: this.state.lastName, age: this.state.age });
        }
        else
            if (name == "firstName") {
                this.setState({ email: this.state.email, firstName: e.target.value, 
                                lastName: this.state.lastName, age: this.state.age });
            }
            else
                if (name == "lastName") {
                    this.setState({ email: this.state.email, firstName: this.state.firstName, 
                                    lastName: e.target.value, age: this.state.age });
                }
                else {
                    this.setState({ email: this.state.email, firstName: this.state.firstName, 
                                    lastName: this.state.lastName, age: Number(e.target.value) });
                }
    }


    handleSubmit(event: FormEvent<HTMLFormElement>): void {
        alert('Podano: ' + this.state.email+ " " + this.state.firstName+ " " + this.state.lastName+ " " + this.state.age);
        
        let locPerson = new SPerson(this.state.email,this.state.firstName,this.state.lastName,this.state.age); 
        this.props.callback(locPerson);        
        event.preventDefault();
    }

    render() {
        return (
            <div>
                <h2>Podaj nową osobę:</h2>
                <form onSubmit={this.handleSubmit}>
                    <label> Email:  <input type="text" name="email" value={this.state.email} onChange={this.handleChange} /> </label>
                    <label> Imię:  <input type="text" name="firstName" value={this.state.firstName} onChange={this.handleChange} /> </label>
                    <label> Nazwisko:  <input type="text" name="lastName" value={this.state.lastName} onChange={this.handleChange} /> </label>
                    <label> Wiek:  <input type="text" name="age" value={this.state.age} onChange={this.handleChange} />       </label>
                    <input type="submit" value="Dodaj" />
                </form>
            </div>
        );
    }
}

export default ReadPerson;
