import React, {useState } from 'react'
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Alert from 'react-bootstrap/Alert';


export default function DeletePersonHook() {

    const [email, setEmail] = useState('');
    const [isError, setIsError] = useState(false);
    const [errorText, setErrorText] = useState("OK");


    //Obsługa przycisku
    const handleSubmit = (event) => {

        if (email.length > 0) {
            //props.callback(email);
            setIsError("OK");
            setErrorText(false);
            event.preventDefault();
        }
        else {
            setIsError("Adres email nie może być pusty.");
            setErrorText(true);
            event.preventDefault()
        }
    }


    return (
        <Container>
            <Alert variant="info">
                <strong>Podaj email osoby do usunięcia:</strong>
            </Alert>

            <Form onSubmit={handleSubmit}>
                <Form.Group>
                    <Form.Label> Email: <input type="text" name='email' value={email} onChange={(e) => setEmail(e.target.value)} /> </Form.Label>
                    {isError && (<div style={{ color: 'red' }}> <Form.Label>{errorText}</Form.Label> </div>)}
                    <p></p>
                    <Button variant="success" type="submit">  Usuń  </Button>
                </Form.Group>
            </Form>
        </Container>

    );

}
