import React, { useState } from 'react'
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import 'bootstrap/dist/css/bootstrap.min.css';



export default function LoginWindowHook() {

    const [show, setShow] = useState(false);
    const [username, setUsername] = useState('user');
    const [password, setPassword] = useState('huser');
    const [musername, setMusername] = useState('user');
    const [mpassword, setMpassword] = useState('huser');


    function handleClose(event) {

        let currentButton = event.currentTarget.textContent;

        this.setState({ show: false });

        if (currentButton === "OK") {
            this.setState({ username: this.state.musername });
            this.setState({ password: this.state.mpassword });
        }
        else {
            this.setState({ musername: this.state.username });
            this.setState({ mpassword: this.state.password });
        }
    }

    function handleCloseHeader() {

        this.setState({ show: false });

        this.setState({ musername: this.state.username });
        this.setState({ mpassword: this.state.password });
    }


    function handleShow() {
        this.setState({ show: true });
    }

    function handleChangeUser(e) {
        this.setState({ musername: e.target.value });
    }

    function handleChangePassword(e) {
        this.setState({ mpassword: e.target.value });
    }


    return (
        <Container>

            <h3>Tekst okna logowania: {this.state.username}, {this.state.password} </h3>

            <Button variant="primary" onClick={this.handleShow}>
                Otwórz okno logowania
            </Button>

            <Modal show={this.state.show} onHide={this.handleCloseHeader}>
                <Modal.Header closeButton>
                    <Modal.Title>Okno logowania</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                        <Form.Group className="auto">
                            <Form.Label>Podaj nazwę użytkownika:</Form.Label>
                            <Form.Control type="text" value={this.state.musername} placeholder="" onChange={this.handleChangeUser} />
                        </Form.Group>
                        <p></p>
                        <Form.Group className="auto">
                            <Form.Label>Podaj hasło:</Form.Label>
                            <Form.Control type="text" value={this.state.mpassword} placeholder="" onChange={this.handleChangePassword} />
                        </Form.Group>
                    </Form >

                </Modal.Body>
                <Modal.Footer>
                    <Button variant="primary" onClick={this.handleClose}>
                        OK
                    </Button>
                    <Button variant="secondary" onClick={this.handleClose}>
                        Anuluj
                    </Button>
                </Modal.Footer>
            </Modal>
        </Container>
    );
}
