import React from 'react';
import { Link } from 'react-router-dom';
import { Routes, Route, BrowserRouter } from 'react-router-dom'; 


//To jest kompletny przykład menu zbudowanego na komponentach funkcyjnych


//Trzy  komponenty
const Home = () => {
    return <p>To jest komponent główny</p>;
}

const Settings = () => {
    return <p>To są moje opcje</p>;
}

const Topics = () => {
    return <p>To są moje tematy</p>;
}


//Komponent nawigacji
const Menu = () => {
    return (
        <Routes>
            <Route path='/' element={<Home />} />
            <Route path='/topics' element={<Topics />} />
            <Route path='/settings' element={<Settings />} />
        </Routes>
    );
}

//Komponent definiujący linki
export default function MenuFunk() {
    return (
        <BrowserRouter>
            <div>
            <p>Wybierz opcję w menu opartym na komponentach funkcyjnych:</p>
                <ul>
                    <li><Link to='/'>Główny</Link></li>
                    <li><Link to='/topics'>Tematy</Link></li>
                    <li><Link to='/settings'>Opcje</Link></li>
                </ul>
                <hr />
                <Menu />
                <hr />
            </div>
        </BrowserRouter>
    )
}
