import React from 'react';
import { Component, ChangeEvent, MouseEvent } from "react";

import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';

import 'bootstrap/dist/css/bootstrap.min.css';

interface Props {    
    callback : (status: boolean) => void;        
};

interface State {
    show: boolean;
    
    username: string;
    password: string;
    
    musername: string;
    mpassword: string;
}


class LoginWindowModal extends Component<Props, State>
{
    constructor(props: Props) {
        super(props);


        this.state = { show: true, username: 'user', password: "huser", musername: 'user', mpassword: "huser" };
     
        this.handleClose = this.handleClose.bind(this);
        //this.handleShow = this.handleShow.bind(this);

        this.handleChangeUser = this.handleChangeUser.bind(this);
        this.handleChangePassword = this.handleChangePassword.bind(this);
        this.handleCloseHeader = this.handleCloseHeader.bind(this);
    }

    
    handleClose(event: MouseEvent<HTMLButtonElement>): void {

        let currentButton = event.currentTarget.textContent;

        this.setState({ show: false });

        if (currentButton === "OK") {
            this.setState({ username: this.state.musername });
            this.setState({ password: this.state.mpassword });
        }
        else {
            this.setState({ musername: this.state.username });
            this.setState({ mpassword: this.state.password });                    
        }

        this.props.callback(false);    
    }

    handleCloseHeader(): void {

        this.setState({ show: false });

        this.setState({ musername: this.state.username });
        this.setState({ mpassword: this.state.password });

        this.props.callback(false);    
    }


    /*
    handleShow(): void {
        this.setState({ show: true });
    }*/

    handleChangeUser(e: ChangeEvent<HTMLInputElement>): void {
        this.setState({ musername: e.target.value });        
    }

    handleChangePassword(e: ChangeEvent<HTMLInputElement>): void {        
        this.setState({ mpassword: e.target.value });
    }

    render() {
        return (
            <div>
                
                <Modal show={this.state.show} onHide={this.handleCloseHeader}>
                    <Modal.Header closeButton>
                        <Modal.Title>Okno logowania</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        <Form>
                            <Form.Group className="auto">
                                <Form.Label>Podaj nazwę użytkownika:</Form.Label>
                                <Form.Control type="text" value={this.state.musername} placeholder="" onChange={this.handleChangeUser} />
                            </Form.Group>
                            <p></p>
                            <Form.Group className="auto">
                                <Form.Label>Podaj hasło:</Form.Label>
                                <Form.Control type="text" value={this.state.mpassword} placeholder="" onChange={this.handleChangePassword} />
                            </Form.Group>
                        </Form >

                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="primary" onClick={this.handleClose}>
                            OK
                        </Button>
                        <Button variant="secondary" onClick={this.handleClose}>
                            Anuluj
                        </Button>
                    </Modal.Footer>
                </Modal>
            </div>
        );
    }
}


export default LoginWindowModal;
