import React, { useState } from 'react'
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import 'bootstrap/dist/css/bootstrap.min.css';


export default function TwoWindowsHook() {

  const [show, setShow] = useState(false);
  const [value, setValue] = useState('');
  const [mvalue, setMvalue] = useState('abc');


  //Obsługa zdarzeń

  function handleClose(event) {

    let currentButton = event.currentTarget.textContent;

    setShow(false);

    if (currentButton === "OK") {
      setValue(mvalue)
    }
    else {
      setMvalue(value)
    }
  }

  function handleCloseHeader() {

    setShow(false);
    setMvalue(value)
  }


  function handleShow() {
    setShow(true);
  }

  function handleChange(e) {
    setMvalue(e.target.value)
  }



  return (
    <div>

      <h3>Tekst okna modalnego: {value} (HOOK)</h3>

      <Button variant="primary" onClick={handleShow}>
        Otwórz okno modalne
      </Button>

      <Modal show={show} onHide={handleCloseHeader}>
        <Modal.Header closeButton>
          <Modal.Title>Okno modalne</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="auto">
              <Form.Label>Podaj tekst:</Form.Label>
              <Form.Control type="text" value={mvalue} placeholder="" onChange={handleChange} />
            </Form.Group>
          </Form >

        </Modal.Body>
        <Modal.Footer>
          <Button variant="primary" onClick={handleClose}>
            OK
          </Button>
          <Button variant="secondary" onClick={handleClose}>
            Anuluj
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}
