import React, { Component } from "react";


interface Props {   
};

interface State { 
    text : string;           
  }

class Form1 extends Component<Props,State> {
    constructor(props : Props) {
        super(props);
        
        this.state = {
            text: "Ala"
        }
       
        this.bset = this.bset.bind(this);
        this.bget = this.bget.bind(this);
                        
    }


    getStorage(key : string) : string{
        let items : string = "NULL";
        let res = localStorage.getItem(key);
        if (res==null)
        {
            items = "NULL";            
        }
        else{
            items = res;
        }
        return items;
    }

    setStorage(key : string,item : string) : void{
        localStorage.setItem(key, JSON.stringify(item));
    }

    bset() {

        let items1 = this.getStorage("ala"); 
        let items2 = this.getStorage("ola"); 
        let items3 = this.getStorage("pola"); 

        this.setStorage("ala",items1+" "+"kowalska");
        this.setStorage("ola",items2+" "+"bazan");
        this.setStorage("pola",items3+" "+"karabanowska");

        /*
        localStorage.setItem('boadusiciel', JSON.stringify(items));
        this.setState((prevState : State) => ({
            text: items
        }));
        */
    }

    bget() {
        
        let items1 = this.getStorage("ala"); 
        let items2 = this.getStorage("ola"); 
        let items3 = this.getStorage("pola"); 

        let list = items1 + " " + items2 + items3;
        
        this.setState((prevState : State) => ({
            text: list
        }));
    }

    
    render() {
        return (

            
            <div>

                <h3>Stan: {this.state.text}</h3>                                 
                <button onClick={this.bset}>ZwiÄ™ksz</button>
                <button onClick={this.bget}>Pokaz</button>
                <br />
                
                <br />
                </div>
          );
    }
}
export default Form1;
