import React, { useState } from 'react';
import { Component, FormEvent, ChangeEvent } from "react";

import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';

import Container from 'react-bootstrap/Container';
import 'bootstrap/dist/css/bootstrap.min.css';

interface Props {
};

interface State {
    show: boolean;
    value: string;
    mtext : string
}


class TwoWindows extends Component<Props, State>
{
    constructor(props: Props) {
        super(props);
        this.state = { show: false, value: '' , mtext: ''};

        this.handleClose = this.handleClose.bind(this);
        this.handleShow = this.handleShow.bind(this);

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }



    handleClose(): void {
        this.setState({ show: false });
    }

    handleShow(): void {
        this.setState({ show: true });
    }

    handleChange(e: ChangeEvent<HTMLInputElement>): void {
        //alert('Zmiana: ' + e.target.value);   
        this.setState({ value: e.target.value });
    }


    handleSubmit(event: FormEvent<HTMLFormElement>): void {
        alert('Podano nastepujacy tekst: ' + this.state.value);
        event.preventDefault();
    }

    render() {
        return (
            <div>

                <h3>Dane z okna modalnego: {this.state.mtext}</h3>

                <Button variant="primary" onClick={this.handleShow}>
                    Otwórz okno modalne
                </Button>

                <Modal show={this.state.show} onHide={this.handleClose}>
                    <Modal.Header closeButton>
                        <Modal.Title>Okno modalne</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>To jest treść okna modalnego

                        <Form onSubmit={this.handleSubmit}>
                            <Form.Group className="auto" onSubmit={this.handleSubmit}>
                                <Form.Label>Podaj tekst:</Form.Label>
                                <Form.Control type="text" value={this.state.value} placeholder="Tekst początkowy" onChange={this.handleChange} />
                            </Form.Group>
                            <p></p>
                            <Button variant="primary" type="submit">  Potwierdź  </Button>
                        </Form >

                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={this.handleClose}>
                            Zamknij
                        </Button>
                    </Modal.Footer>
                </Modal>
            </div>
        );
    }
}


export default TwoWindows;
