import React, {useState } from 'react'


export default function CounterAppHook() {


    const [status, setStatus] = useState("OK");
    const [textArea, setTextArea] = useState("Brak danych");
    const [newClientname, setNewClientname] = useState("nowak");
    const [newDate, setNewDate] = useState("2022-10-30");
    const [newDescription, setNewDescription] = useState("Zakup USD");
    const [newAmount, setNewAmount] = useState("345.1");
    const [transferIDToDelete, setTransferIDToDelete] = useState("1");
    const [userName, setUserName] = useState("bazan@gmail.com");
    const [password, setPassword] = useState("rzeszow");
    const [modalShow, setModalShow] = useState(false);
    const [modalNewUsername, setModalNewUsername] = useState("bazan@gmail.com");
    const [modalNewPassword, setModalNewPassword] = useState("rzeszow");


    //ROLA: ADMIN
    //userName: "admin@gmail.com", password: "polska"         

    //ROLA: CLIENT
    //userName: "nowak@gmail.com", password: "nowaczek"         

    //ROLA: ADMIN i CLIENT
    //userName: "bazan@gmail.com", password: "rzeszow"         


    //Pobranie danych z magazynu
    function getStorage(key) {
        let items = "NULL";
        let res = localStorage.getItem(key);
        if (res == null) items = "NULL";
        else items = res;
        return items;
    }

    //Wstawienie danych do magazynu
    function setStorage(key, item) {
        localStorage.setItem(key, item);
    }

    //Dodanie przelewu na serwer
    async function addTransfer() {

        let clientname = newClientname;
        let date = newDate;
        let description = newDescription;
        let amount = newAmount;


        if ((clientname.length > 0) && (date.length > 0) && (description.length > 0) && (amount.length > 0)) {


            let token = userName + ":" + password;
            var etoken = window.btoa(token);
            var basicAuth = 'Basic ' + etoken;

            await axios.post('http://localhost:8081/padmin/addtransfer/',
                { clientname, date, description, amount }, { headers: { authorization: basicAuth } } //Podajemy parametry
            )
                .then(response => {
                    let text = response.data;
                    setStatus(text);
                }).catch(err => {
                    let myerror = "Błąd połączenia sieciowego." + err;
                    setStatus(myerror);
                });
        }
        else {
            setStatus("Żadna z danych wstawianego przelewu nie może być pusta");
        }
    }


    //Pobranie listy przelewow z serwera
    async function getTransfers() {

        let token = userName + ":" + password;
        var etoken = window.btoa(token);
        var basicAuth = 'Basic ' + etoken;


        await axios.post('http://localhost:8081/pclient/gettransfers/', {}, { headers: { authorization: basicAuth } })
            .then(response => {

                let transferListText = ""

                response.data.forEach((e) => {
                    let lid = e.id;
                    let lclientname = e.clientname; let ldate = e.date; let ldescription = e.description; let lamount = e.amount;

                    let firstPartText = lclientname.substring(0, ("ERROR:").length);


                    if (firstPartText !== "ERROR:") {
                        transferListText = transferListText + lid + ", " + lclientname + ", " + ldate + ", " + ldescription + ", " + lamount + "\n";
                        textArea(transferListText);
                        setStatus("OK");
                    }
                    else {
                        let secondPartText = lclientname.substring(("ERROR:").length, lclientname.length);
                        transferListText = lclientname + "\n";
                        textArea(transferListText);
                        setStatus(secondPartText);
                    }
                })

            }).catch(err => {

                let myerror = "Błąd połaczenia sieciowego." + err;
                setStatus(myerror);
            });

    }


    async function deleteTransfer() {

        let transferidtodelete = transferIDToDelete;

        if (transferidtodelete.length > 0) {

            let token = userName + ":" + password;
            var etoken = window.btoa(token);
            var basicAuth = 'Basic ' + etoken;

            await axios.post('http://localhost:8081/padmin/deletetransfer/',
                { transferidtodelete }, { headers: { authorization: basicAuth } } //Podajemy parametr
            )
                .then(response => {
                    let text = response.data;
                    setStatus(text);
                }).catch(err => {
                    let myerror = "Błąd połaczenia sieciowego." + err;
                    setStatus(myerror);
                });
        }
        else {
            setStatus("ID przelewu nie może być puste");
        }
    }


    function handleSubmit(event) {

        addTransfer();
        event.preventDefault(); //Dla zapobiegniecia ponownemu załadowaniu/odświeżeniu przeglądarki
    }


    //--------------------------------------------
    //Obsługa okna wprowadania uzytkownika i hasla

    function handleClose(event) {

        let currentButton = event.currentTarget.textContent;

        setModalShow(false);

        if (currentButton === "OK") {

            setUserName(modalNewUsername);
            setPassword(modalNewPassword);
        }
        else {
            setUserName(userName);
            setPassword(password);
        }
    }



    function handleCloseHeader() {

        setModalShow(false);
        setUserName(userName);
        setPassword(password);
    }


    function handleChangeUser(e) {
        setModalNewUsername(e.target.value)
    }

    function handleChangePassword(e) {
        setModalNewPassword(e.target.value)
    }

    function handleShow() {
        setModalShow(true);
    }

    //-----------------------------------------------


    return (

        <Container>

            <Alert variant="info">
                <Alert.Heading><strong>Klient dostępu do przelewów w banku z użyciem REST (z autoryzacją, HOOK) </strong></Alert.Heading>
                Lokalizacja bazy: http://localhost:8081/
            </Alert>

            <p></p>

            <h3>Status: {status}</h3>

            <p></p>

            <Form.Label> Dane nowego przelewu: </Form.Label>

            <Form onSubmit={handleSubmit}>
                <Form.Group className="auto" onSubmit={handleSubmit}>
                    <Form.Label> Klient: <input type="text" name='newClientname' value={newClientname} onChange={(e) => setNewClientname(e.target.value)} /> </Form.Label>
                    {' '}
                    <Form.Label> Data przelewu: <input type="text" name='newDate' value={newDate} onChange={(e) => setNewDate(e.target.value)} /> </Form.Label>
                    {' '}
                    <Form.Label> Opis przelewu:: <input type="text" name='newDescription' value={newDescription} onChange={(e) => setNewDescription(e.target.value)} /> </Form.Label>
                    {' '}
                    <Form.Label> Kwota: <input type="text" name='newAmount' value={newAmount} onChange={(e) => setNewAmount(e.target.value)} /> </Form.Label>
                    {' '}
                    <Button variant="primary" type="submit">  Dodaj  </Button>
                </Form.Group>
                <p></p>
            </Form >

            <Form.Group className="auto" onSubmit={handleSubmit}>
                <Form.Label> Podaj id przelewu do usunięcia: <input type="text" name='transferIDToDelete' value={transferIDToDelete} onChange={(e) => setTransferIDToDelete(e.target.value)} /> </Form.Label>
                {' '}
                <Button variant="primary" onClick={deleteTransfer}>  Usuń  </Button>
            </Form.Group>

            <p></p>


            <Button variant="primary" onClick={getTransfers}>  Odczytaj aktualną listę przelewów  </Button>

            <p></p>

            <Form.Control as="textarea" rows={8} type="text" value={textArea} placeholder="" onChange={(e) => setTextArea(e.target.value)} spellCheck="false" />

            <p></p>

            <p></p>


            <p>Aktualnie podany użytkownik i hasło: {userName}, {password}</p>

            <Button variant="primary" onClick={handleShow}> Aktualizuj dane do autoryzacji </Button>  {' '}

            <p></p>

            <Modal show={modalShow} onHide={handleCloseHeader}>
                <Modal.Header closeButton>
                    <Modal.Title>Logowanie</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                        <Form.Group className="auto">
                            <Form.Label>Użytkownik:</Form.Label>
                            <Form.Control type="text" value={modalNewUsername} placeholder="" onChange={(e) => setModalNewUsername(e.target.value)} />
                        </Form.Group>
                        <Form.Group className="auto">
                            <Form.Label>Hasło:</Form.Label>
                            <Form.Control type="text" value={modalNewPassword} placeholder="" onChange={(e) => setModalNewPassword(e.target.value)} />
                        </Form.Group>
                    </Form >

                </Modal.Body>
                <Modal.Footer>
                    <Button variant="primary" onClick={handleClose}>
                        OK
                    </Button>
                    <Button variant="secondary" onClick={handleClose}>
                        Anuluj
                    </Button>
                </Modal.Footer>
            </Modal>

        </Container>

    );


}
