import React, {useState } from 'react'

import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';

import StringSet from "./StringSet"

export default function CheckboxFormHook() {
    
    const [isAutobusChecked, setIsAutobusChecked] = React.useState(true);
    const [isPociagChecked, setIsPociagChecked] = React.useState(false);
    const [isSamochodChecked, setIsSamochodChecked] = React.useState(false);

    //Obsługa przycisku
    const handleSubmit = (event) => {
        event.preventDefault();
        alert('Aktualny stan: ' + isAutobusChecked);
    }
    

    return (
        <Container>
            <h3>aa Aplikacja demonstrująca pole wyboru "kilka z wielu" (HOOK)</h3>
            <Form onSubmit={handleSubmit}>
                <Form.Group className="mb-3">
                    <Form.Label>Jakimi środkami transportu podróżujesz na codzień:</Form.Label>
                    <Form.Check
                        type={"checkbox"}
                        label="Autobus"
                        value={"Autobus"}      
                        checked={isAutobusChecked}                  
                        onChange={setIsAutobusChecked}>
                    </Form.Check>
                    <Form.Check
                        type={"checkbox"}
                        label="Pociag"
                        value={"Pociag"}                   
                        checked={isPociagChecked}                       
                        onChange={setIsPociagChecked}>
                    </Form.Check>
                    <Form.Check
                        type={"checkbox"}
                        label="Samochod"
                        value={"Samochod"}                 
                        checked={isSamochodChecked}                         
                        onChange={setIsSamochodChecked}>
                    </Form.Check>
                </Form.Group>
                <Button variant="primary" type="submit">  Potwierdź  </Button>
            </Form>
        </Container>
    );

}
