import  SBook from "./SBook"

class SBookList{

    public books : SBook[];
    
    constructor(){
        this.books = [];        
    }

    public add(book : SBook) : void {
        this.books.push(book);
    }

    public get(index : number) : SBook {
        return this.books[index];
    }

    public size() : number {
        return this.books.length;
    }

    public remove(index : number) : void {

        if ((index >=0)&&(index<this.books.length)) {
            this.books.splice(index, 1);
        }
    }

    public getCopy() : SBookList {

        let locBookList : SBookList = new SBookList();

        if (this.size()>0)
        {
            for (let i : number=0; i<this.size(); i=i+1)
            {
                let book = this.get(i);
                locBookList.add(book);                
            }            
        }

        return locBookList;
    }

    //Utworzenie bazy poczatkowej ksiazek
    public build() : void {
        let b1 = new SBook("Hobbit","Tolkien","455");
        let b2 = new SBook("Rok 1984","Orwell","455");
        let b3 = new SBook("Władca Pierścieni","Tolkien","669");
        let b4 = new SBook("Stary człowiek i morze","Hemingway","226");

        this.add(b1);this.add(b2);this.add(b3);this.add(b4);
    }

    public selectBooks() : void {

        if (this.size()>0)
        {

            let nbooks : SBook[] = [];  

            for (let i : number=0; i<this.size(); i=i+1)
            {
                let book = this.get(i);
                let author = book.author;
                
                if (author=="Tolkien"){
                    nbooks.push(book);        
                }
            }            

            this.books = nbooks;

        }
        
    }

    public filtrBooks(filtrText : string) : void {

        if (this.size()>0)
        {

            let filter = filtrText.toUpperCase();

            let nbooks : SBook[] = [];  

            for (let i : number=0; i<this.size(); i=i+1)
            {
                let book = this.get(i);
                let author = book.author;
                let title = book.title;
                let text = author + " " + title;
                text = text.toUpperCase();

                if (text.includes(filter))
                {
                    nbooks.push(book);        
                }
            }            

            this.books = nbooks;
        }
        
    }

    public addBook() : void {

        let b = new SBook("Pan Tadeusz","Mickiewicz","345");        
        this.add(b);
    }

    
}

export default SBookList;
