import React, { Component, FormEvent, ChangeEvent } from "react";
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Alert from 'react-bootstrap/Alert';

import SPerson from "./SPerson"

interface Props {
    callback: (p: SPerson) => void;
};

interface State {
    email: string;
    firstName: string;
    lastName: string;
    age: number;
}


class ReadPerson extends Component<Props, State>
{
    constructor(props: Props) {
        super(props);
        this.state = { email: 'pola@gmail.com', firstName: 'Pola', lastName: 'Polańska', age: 22 };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }


    handleChange(e: ChangeEvent<HTMLInputElement>): void {

        const name = e.target.name;
        if (name == "email") {
            this.setState({
                email: e.target.value, firstName: this.state.firstName,
                lastName: this.state.lastName, age: this.state.age
            });
        }
        else
            if (name == "firstName") {
                this.setState({
                    email: this.state.email, firstName: e.target.value,
                    lastName: this.state.lastName, age: this.state.age
                });
            }
            else
                if (name == "lastName") {
                    this.setState({
                        email: this.state.email, firstName: this.state.firstName,
                        lastName: e.target.value, age: this.state.age
                    });
                }
                else {
                    this.setState({
                        email: this.state.email, firstName: this.state.firstName,
                        lastName: this.state.lastName, age: Number(e.target.value)
                    });
                }
    }


    handleSubmit(event: FormEvent<HTMLFormElement>): void {
        //alert('Podano: ' + this.state.email + " " + this.state.firstName + " " + this.state.lastName + " " + this.state.age);
        let locPerson = new SPerson(this.state.email, this.state.firstName, this.state.lastName, this.state.age);
        this.props.callback(locPerson);
        event.preventDefault();
    }

    render() {
        return (
            <Container>

                <Alert variant="info">
                    <strong>Podaj nową osobę do bazy danych:</strong>
                </Alert>

                <Form onSubmit={this.handleSubmit}>
                    <Form.Group>
                        <Form.Label> Email: <input type="text" name='email' value={this.state.email} /> </Form.Label>
                        {' '}
                        <Form.Label> Imię: <input type="text" name='firstName' value={this.state.firstName} /> </Form.Label>
                        {' '}
                        <Form.Label> Nazwisko: <input type="text" name='lastName' value={this.state.lastName} /> </Form.Label>
                        {' '}
                        <Form.Label> Wiek: <input type="text" name='email' value={this.state.age} /> </Form.Label>
                        {' '}
                        <Button variant="primary" type="submit">  Dodaj  </Button>
                    </Form.Group>
                    <p></p>
                </Form>
            </Container>
        );
    }
}

export default ReadPerson;
