import React, {useState } from 'react'

import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';


export default function RadiobuttonForm() {

  const [options, setOptions] = useState(new StringSet());

  
  //Obsługa przycisku
  const handleSubmit = (event) => {
    event.preventDefault();
    alert('Aktualny stan: ' + options);
  }

  const handleChange = (event) => {
  
    alert('Wybrano: ' + event.target.value);

    /*
    if (this.state.selectedOptions.member(e.target.value)) {
      this.state.selectedOptions.delete(e.target.value);
    }
    else {
      this.state.selectedOptions.add(e.target.value);
    }

    this.setState({
      selectedOptions: this.state.selectedOptions
    });
    */
  }

  
  return (
    <Container>
      <h3>Aplikacja demonstrująca pole wyboru "kilka z wielu" (HOOK)</h3>
      <Form onSubmit={handleSubmit}>
        <Form.Group className="mb-3">
          <Form.Label>Jakimi środkami transportu podróżujesz na codzień:</Form.Label>
          <Form.Check
            type={"checkbox"}
            label="Autobus"
            value={"Autobus"}
            checked={options.member("Autobus")}
            onChange={handleChange}>
          </Form.Check>
          <Form.Check
            type={"checkbox"}
            label="Pociag"
            value={"Pociag"}
            checked={options.member("Pociag")}
            onChange={handleChange}>
          </Form.Check>
          <Form.Check
            type={"checkbox"}
            label="Samochod"
            value={"Samochod"}
            checked={options.member("Samochod")}
            onChange={handleChange}>
          </Form.Check>
        </Form.Group>
        <Button variant="primary" type="submit">  Potwierdź  </Button>
      </Form>
    </Container>
  );


    return (

        <Container>

            <h3>Aplikacja demonstrująca pole wyboru "jeden z wielu" (HOOK)</h3>

            <Form onSubmit={handleSubmit}>
                <Form.Group className="mb-3">
                    <Form.Label>Wybierz swój ulubiony kolor:</Form.Label>
                    <Form.Check
                        type={"radio"}
                        label="Red"
                        value={"Red"}
                        onChange={(e) => setOption(e.target.value)}
                        checked={option === "Red"}>
                    </Form.Check>
                    <Form.Check
                        type={"radio"}
                        label="Green"
                        value={"Green"}
                        onChange={(e) => setOption(e.target.value)}
                        checked={option === "Green"}>
                    </Form.Check>
                    <Form.Check
                        type={"radio"}
                        label="Blue"
                        value={"Blue"}
                        onChange={(e) => setOption(e.target.value)}
                        checked={option === "Blue"}>
                    </Form.Check>
                </Form.Group>
                <Button variant="primary" type="submit">  Potwierdź  </Button>
            </Form>
        </Container>
    );

}
