import React, {useState } from 'react'

import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';

import StringSet from "./StringSet"

export default function CheckboxFormHook() {

    const opt = []
    opt.add("Autobus");
    const [options, setOptions] = useState(opt);


    //Obsługa przycisku
    const handleSubmit = (event) => {
        event.preventDefault();
        alert('Aktualny stan: ' + options);
    }

    const handleChange = (event) => {

        if (e.target.checked) {
            setOptions([...options, event.target.value]);
        }
        else{
            setOptions(options.filter((item) => item !== event.target.value));
        }
    }

    return (
        <Container>
            <h3>aa Aplikacja demonstrująca pole wyboru "kilka z wielu" (HOOK)</h3>
            <Form onSubmit={handleSubmit}>
                <Form.Group className="mb-3">
                    <Form.Label>Jakimi środkami transportu podróżujesz na codzień:</Form.Label>
                    <Form.Check
                        type={"checkbox"}
                        label="Autobus"
                        value={"Autobus"}                        
                        onChange={handleChange}>
                    </Form.Check>
                    <Form.Check
                        type={"checkbox"}
                        label="Pociag"
                        value={"Pociag"}                        
                        onChange={handleChange}>
                    </Form.Check>
                    <Form.Check
                        type={"checkbox"}
                        label="Samochod"
                        value={"Samochod"}                        
                        onChange={handleChange}>
                    </Form.Check>
                </Form.Group>
                <Button variant="primary" type="submit">  Potwierdź  </Button>
            </Form>
        </Container>
    );

}
