import React, { Component, FormEvent, ChangeEvent } from "react";
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

class ManyElemFormValid extends Component
{
    constructor(props) {
        super(props);
        this.state = {
            name: "a",
            age: "0",
            gender: "N",

            isErrorName: false,
            errorNameText: "OK",
            isErrorAge: false,
            errorAgeText: "OK",
            isErrorGender: false,
            errorGenderText: "OK"
        }

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }


    handleChange(evt) {

        const name = evt.target.name;

        if (name == "name") {

            this.setState({ name: evt.target.value });
        }
        else
            if (name == "age") {
                this.setState({ age: evt.target.value });
            }
            else {
                this.setState({ gender: evt.target.value });
            }
    }


    nameValidation(name){
        if ((name.length < 2) || (name.length > 20)) {
            return "Za krótkie lub za długie imie";
        }
        else {
            return "OK";
        }
    }

    ageValidation(age) {
        let iage = Number(age);        
        if ((Number.isNaN(iage)) || (iage < 1) || (iage > 140)) {
            return "Błędne oznaczenie wieku: " + age;
        }
        else {
            return "OK";
        }
    }

    genderValidation(gender) {
        if ((gender !== "K") && (gender !== "M")) {
            return "Błędne oznaczenie płci: " + gender + " (podaj 'K' albo 'M')";
        }
        else {
            return "OK";
        }
    }

    handleSubmit(event) {

        let resultForName = this.nameValidation(this.state.name);
        if (resultForName !== "OK") {
            this.setState((prevState) => ({
                isErrorName: true, errorNameText: resultForName
            }));
        }
        else {
            this.setState({ isErrorName: false, errorNameText: "OK" });
        }

        let resultForAge = this.ageValidation(this.state.age);
        if (resultForAge !== "OK") {
            this.setState((prevState) => ({
                isErrorAge: true, errorAgeText: resultForAge
            }));
        }
        else {
            this.setState({ isErrorAge: false, errorAgeText: "OK" });
        }

        let resultForGender = this.genderValidation(this.state.gender);
        if (resultForGender !== "OK") {
            this.setState((prevState) => ({
                isErrorGender: true, errorGenderText: resultForGender
            }));
        }
        else {
            this.setState({ isErrorGender: false, errorGenderText: "OK" });
        }

        event.preventDefault();

    }

    render() {

        return (

            <Container>

                <h2>Aplikacja demonstrująca okno z formularzem wieloelementowym z walidacją poprawności danych</h2>

                <Form onSubmit={this.handleSubmit}>

                    <h3>Wynik wprowadzania danych: {this.state.name}, {this.state.age}, {this.state.gender}</h3>

                    <Form.Group className="auto">
                        <Form.Label>Podaj imię:</Form.Label>
                        <Form.Control type="text" name='name' value={this.state.name} placeholder="" onChange={this.handleChange} />
                        {this.state.isErrorName && (<div style={{ color: 'red' }}> <Form.Label>{this.state.errorNameText}</Form.Label> </div>)}
                    </Form.Group>

                    <div><p></p></div>

                    <Form.Group className="auto">
                        <Form.Label>Podaj wiek:</Form.Label>
                        <Form.Control type="text" name='age' value={this.state.age} placeholder="" onChange={this.handleChange} />
                        {this.state.isErrorAge && (<div style={{ color: 'red' }}> <Form.Label>{this.state.errorAgeText}</Form.Label>
                        </div>)}
                    </Form.Group>

                    <div><p></p></div>

                    <Form.Group className="auto">
                        <Form.Label>Podaj płeć:</Form.Label>
                        <Form.Control type="text" name='city' value={this.state.gender} placeholder="" onChange={this.handleChange} />
                        {this.state.isErrorGender && (<div style={{ color: 'red' }}> <Form.Label>{this.state.errorGenderText}</Form.Label> </div>)}
                    </Form.Group>

                    <div><p></p></div>

                    <Button variant="primary" type="submit">  Potwierdź  </Button>
                </Form >
            </Container>

        );
    }
}

export default ManyElemFormValid;
