import React, { useState } from 'react';
import { Component, FormEvent, ChangeEvent, MouseEvent } from "react";

import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';

import Container from 'react-bootstrap/Container';
import 'bootstrap/dist/css/bootstrap.min.css';

interface Props {
};

interface State {
    show: boolean;
    value: string;
    mvalue: string;
}


class TwoWindows extends Component<Props, State>
{
    constructor(props: Props) {
        super(props);
        this.state = { show: false, value: '', mvalue: '' };

        this.handleClose = this.handleClose.bind(this);
        this.handleShow = this.handleShow.bind(this);

        this.handleChange = this.handleChange.bind(this);
    }


    /*
        handleClose(): void {
            this.setState({ show: false });
            this.setState({ value: this.state.mvalue });
        }
    
        handleCancel(): void {        
        }
    */
    handleClose(event: MouseEvent<HTMLButtonElement>): void {

        let currentButton = event.currentTarget.title;

        if (currentButton==="OK")
        {
            this.setState({ show: false });
            this.setState({ value: this.state.mvalue });    
        }

        //alert('Zmiana: ' + e.currentTarget.className+" "+e.currentTarget.id+" "+e.currentTarget.name+" >"+e.currentTarget.title+" "+e.currentTarget.textContent+" "+e.currentTarget.value+" "+e.currentTarget.tagName);
        //this.setState({ mvalue: e.target.value });
    }

    handleShow(): void {
        this.setState({ show: true });
    }

    handleChange(e: ChangeEvent<HTMLInputElement>): void {
        //alert('Zmiana: ' + e.currentTarget);   
        this.setState({ mvalue: e.target.value });
    }


    render() {
        return (
            <div>

                <h3>Dane z okna modalnego: {this.state.value}</h3>

                <Button variant="primary" onClick={this.handleShow}>
                    Otwórz okno modalne
                </Button>

                <Modal show={this.state.show}>
                    <Modal.Header closeButton>
                        <Modal.Title>Okno modalne</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        <Form>
                            <Form.Group className="auto">
                                <Form.Label>Podaj tekst:</Form.Label>
                                <Form.Control type="text" value={this.state.mvalue} placeholder="Tekst początkowy" onChange={this.handleChange} />
                            </Form.Group>
                        </Form >

                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={this.handleClose}>
                            OK
                        </Button>

                        <Button variant="secondary" onClick={this.handleClose}>
                            Anuluj
                        </Button>


                    </Modal.Footer>
                </Modal>
            </div>
        );
    }
}


export default TwoWindows;
