import React, {useState } from 'react'

import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';


export default function ListFormHook() {

  const [myValue, setMyValue] = useState("kokosowy");

  //Obsługa przycisku
  
  
  const handleSubmit = (event) => {
    alert('Wybrano smak: ' + myValue);    
    event.preventDefault();    
  }

    return (
        <Container>
            <h3>Aplikacja demonstrująca pole wyboru z listy (HOOK)</h3>

            <Form onSubmit={handleSubmit}>
                <Form.Group className="mb-3">
                    <Form.Label>Wybierz swój ulubiony smak:</Form.Label>
                    <Form.Select value={myValue} onChange={(e) => setMyValue(e.target.value)}>
                        <option value="grejpfrutowy">Grejpfrutowy</option>
                        <option value="limonkowy">Limonkowy</option>
                        <option value="kokosowy">Kokosowy</option>
                        <option value="mango">Mango</option>
                    </Form.Select>
                </Form.Group>
            </Form >
        </Container>
    );
}
