import React, { Component, FormEvent, ChangeEvent } from "react";

interface Props {
};

interface State {
    valueFirstName: string;
    valueLastName: string;
}


class DNameForm extends Component<Props, State>
{
    constructor(props: Props) {
        super(props);
        this.state = {
            valueFirstName: "",
            valueLastName: ""
        }

        this.handleChange = this.handleChange.bind(this);        
        this.handleSubmit = this.handleSubmit.bind(this);
    }


  handleChange(evt: ChangeEvent<HTMLInputElement>) {

    const name = evt.target.name;
    if (name=="firstName")
    {
        this.setState({valueFirstName: evt.target.value});      
    }
    else
    {
        this.setState({valueLastName: evt.target.value});      
    }
    
}


handleSubmit(event : FormEvent<HTMLFormElement>) {
    alert('Podano nastepujace imie i nazwisko: ' + this.state.valueFirstName + " " + this.state.valueLastName);
    event.preventDefault();
}

render() {

    return (
        <form onSubmit={this.handleSubmit}>
            <label> Imie: <input type="text" name='firstName' value={this.state.valueFirstName} onChange={this.handleChange} />  </label>
            <label> Nazwisko: <input type="text" name='lastName' value={this.state.valueLastName} onChange={this.handleChange} />  </label>

            <input type="submit" value="Wyslij" />
        </form>
    );
}
}

export default DNameForm;
