import React, {useState } from 'react'

import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';


export default function DNameFormHook() {


  const [state, setState] = useState({ valueFirstName: "Jan", valueLastName: "Kowal" });  

  const [myValue, setMyValue] = useState("abc");

  
  //Obsługa przycisku
  const handleSubmit = (event) => {
    event.preventDefault();
    alert('Podano nastepujace teksty: ' + state.valueFirstName+ " "+ state.valueLastName);
  }

  //Obsluga aktualizacji stanu
  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setState((prevProps) => ({
      ...prevProps,
      [name]: value
    }));
  };



  return (
    <Container>
      <h3>Aplikacja demonstrująca pole wprowadzanie tekstu jednolinijkowego (HOOK)</h3>

      <Form onSubmit={handleSubmit}>
        <Form.Group className="auto">
          <Form.Label>Podaj tekst:</Form.Label>          
          <Form.Control type="text" value={state.valueFirstName} placeholder="" onChange={handleInputChange} />          
        </Form.Group>
        <p></p>
        <Button variant="primary" type="submit">  Potwierdź  </Button>
      </Form >
    </Container>
  );
}
