import React, { Component, ChangeEvent } from "react";
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';

import Footer from "./footer";
import Header from "./header";
import BookDescription from "./BookDescription";


import SBookList from "./SBookList"

//Aplikacja pokasująca filtrowanie książek w komponencie

interface Props {
};

interface State {
    filtrText: string;
    allBooks: SBookList;
    selectedBooks: SBookList;
};


class BookApp extends Component<Props, State> {

    constructor(props: Props) {
        super(props);

        this.state = {
            filtrText: "",
            allBooks: new SBookList(),
            selectedBooks: new SBookList(),
        }

        this.state.allBooks.build();
        this.state.selectedBooks.build();

        this.selectBooks = this.selectBooks.bind(this);
        this.filtrBooks = this.filtrBooks.bind(this);
        this.addNewBook = this.addNewBook.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }


    selectBooks() {
        let locCopyBookList = this.state.allBooks.getCopy();
        locCopyBookList.selectBooks();
        this.setState({ selectedBooks: locCopyBookList });
    }

    filtrBooks() {
        let locCopyBookList = this.state.allBooks.getCopy();
        if (this.state.filtrText.length > 0) {
            locCopyBookList.filtrBooks(this.state.filtrText);
        }
        this.setState({ selectedBooks: locCopyBookList });
    }


    addNewBook(): void {

        this.state.allBooks.addBook();
        this.state.selectedBooks.addBook();

        this.setState((prevState) => ({
            allBooks: prevState.allBooks,
            selectedBooks: prevState.selectedBooks,
        }));
    }

    handleChange(evt: ChangeEvent<HTMLInputElement>) {

        const name = evt.target.name;

        if (name === "filtrText") this.setState({ filtrText: evt.target.value });
    }


    render() {
        return (
            <Container>
                <Header />

                <Form.Group>
                    <Button variant="primary" onClick={this.selectBooks}>  Pozostaw książki tylko Tolkiena  </Button>
                    <p></p>
                    <Button variant="primary" onClick={this.addNewBook}>  Dodaj jakąś ksiażkę  </Button>
                </Form.Group>


                {this.state.selectedBooks.books.map((lbook, index) => (
                    <BookDescription title={lbook.title} author={lbook.author} pages={lbook.pages} />
                ))}

                <div>
                    <label> Tekst do filtra: <input type="text" name='filtrText' value={this.state.filtrText} onChange={this.handleChange} />  </label>
                    <button onClick={this.filtrBooks}>Filtruj książki</button> <br />
                </div>

                <Footer />
            </Container>
        );
    }
}
export default BookApp;
