import React, { Component, FormEvent, ChangeEvent } from "react";
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Alert from 'react-bootstrap/Alert';

interface Props {    
    callback : (email: string) => void;        
};

interface State {
    email: string; 
    errors : string;   
}


class DeletePerson extends Component<Props, State>
{
    constructor(props: Props) {
        super(props);
        this.state = { email: '', errors: ""};
        
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }


    handleChange(e: ChangeEvent<HTMLInputElement>): void {

        this.setState({ email: e.target.value});
    }


    handleSubmit(event: FormEvent<HTMLFormElement>): void {                        
        if (this.state.email.length>0)
        {
            this.props.callback(this.state.email);        
            this.setState({ errors: ""});            
            event.preventDefault();
        }
        else
        {
            this.setState({ errors: "Adres email nie może być pusty."});            
            event.preventDefault()
        }
    }

    render() {
        return (

            <Container>

                <Alert variant="info">
                    <strong>Podaj email osoby do usuniecia:</strong>
                </Alert>

                <Form onSubmit={this.handleSubmit}>
                    <Form.Group>
                        <Form.Label> Email: <input type="text" name='email' value={this.state.email} onChange={this.handleChange} /> </Form.Label>
                        <label> {this.state.errors} </label>
                        {' '}                        
                        <Button variant="primary" type="submit">  Usuń  </Button>
                    </Form.Group>
                </Form>
            </Container>


            
        );
    }
}

export default DeletePerson;
