import React, { Component, useState } from "react";
import Container from 'react-bootstrap/Container';
import Table from 'react-bootstrap/Table'; 
import Form from 'react-bootstrap/Form';
import Alert from 'react-bootstrap/Alert';
import 'bootstrap/dist/css/bootstrap.min.css';

import Footer from "./footer";
import Header from "./header";
import PersonDescriptionHook from "./PersonDescriptionHook";

import SPersonHook from "./SPersonHook"
import SPersonListHook from "./SPersonListHook"
import ReadPersonHook from "./ReadPersonHook"
import DeletePersonHook from "./DeletePersonHook"


//Baza danych osób w pamięci RAM oparta na 4 głównych komponentach klasowych (PDatabaseAppHook, PersonDescriptionHook, ReadPersonHook, DeletePersonHook)
//Korzysta także z 2 kompotentów pomocniczych:  Header i Footer
//Pokazuje obsługę bazy danych w aplikacji typu SPA


export default function PDatabaseAppHook(props) {


    let pList = new SPersonListHook();
    pList.build();
    const [personList, setPersonList] = useState(pList);
    const [status, setStatus] = useState("Start");
    const [isError, setIsError] = useState(false);


    function addNewPerson(p) {

        let locList = personList.copy(); 
        locList.add(p);
        setPersonList(locList)
        setStatus("Dodano osobę: " + p.firstname + " " + p.lastname)
        setIsError(false);        
    }

    function deletePerson(email) {

        let result = personList.deletePerson(email);
        
        if (!result) {
            setStatus("Brak osoby o adresie email: " + email)
            setIsError(true);
        }
        else {

            setStatus("Usunieto osobę o adresie email: " + email);
            setIsError(false);
        }        
    }

    return (
        <Container>
            <Header />

            {!isError && <Alert variant="success"> <strong>Status: {status}</strong> </Alert>}
            {isError && <Alert variant="danger"> <strong>Status: {status}</strong> </Alert>}


            <Table striped bordered>
                <thead>
                    <tr>
                        <th>Email</th>
                        <th>Imię</th>
                        <th>Nazwisko</th>
                        <th>Wiek</th>
                    </tr>
                </thead>
                <tbody>
                    {personList.personList.map((lperson, index) => (
                        <PersonDescriptionHook email={lperson.email} firstname={lperson.firstname} lastname={lperson.lastname} age={lperson.age} />
                    ))}
                </tbody>
            </Table>

            <ReadPersonHook callback={addNewPerson} />

            <DeletePersonHook callback={deletePerson} />

            <Footer />
        </Container>
    );


}
