import React, {useState } from 'react'

import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';


//Wybór "kilka z wielu" (HOOK)

export default function CheckboxFormHook() {
    
    const [isAutobusChecked, setIsAutobusChecked] = useState(true);
    const [isPociagChecked, setIsPociagChecked] = useState(false);
    const [isSamochodChecked, setIsSamochodChecked] = useState(false);

    //Obsługa przycisku
    const handleSubmit = (event) => {
        event.preventDefault();
        alert('Aktualny stan: Autobus->' + isAutobusChecked + " Pociag->" + isPociagChecked + " Samochod->" + isSamochodChecked);
    }

    function handleChangeAutobus() {setIsAutobusChecked(!isAutobusChecked)}
    function handleChangePociag() {setIsPociagChecked(!isPociagChecked)}
    function handleChangeSamochod() {setIsSamochodChecked(!isSamochodChecked)}


    return (
        <Container>
            <h3>Aplikacja demonstrująca pole wyboru "kilka z wielu" (HOOK)</h3>
            <Form onSubmit={handleSubmit}>
                <Form.Group className="mb-3">
                    <Form.Label>Jakimi środkami transportu podróżujesz na codzień:</Form.Label>
                    <Form.Check
                        type={"checkbox"}
                        label="Autobus"
                        value={"Autobus"}      
                        checked={isAutobusChecked}                  
                        onChange={handleChangeAutobus}>                        
                    </Form.Check>
                    <Form.Check
                        type={"checkbox"}
                        label="Pociag"
                        value={"Pociag"}                   
                        checked={isPociagChecked}                       
                        onChange={handleChangePociag}>
                    </Form.Check>
                    <Form.Check
                        type={"checkbox"}
                        label="Samochod"
                        value={"Samochod"}                 
                        checked={isSamochodChecked}                         
                        onChange={handleChangeSamochod}>
                    </Form.Check>
                </Form.Group>
                <Button variant="primary" type="submit">  Potwierdź  </Button>
            </Form>
        </Container>
    );

}
