import React, {useState } from 'react'

import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';

import StringSet from "./StringSet"

export default function RadiobuttonForm() {

    const oo = new StringSet();
    oo.add("Autobus");
    const [options, setOptions] = useState(oo);


    //Obsługa przycisku
    const handleSubmit = (event) => {
        event.preventDefault();
        alert('Aktualny stan: ' + options.getToString());
    }

    const handleChange = (event) => {

        if (options.member(event.target.value)) {
            options.delete(event.target.value);
        }
        else {
            options.add(event.target.value);
        }

        setOptions(options);
    }

    return (
        <Container>
            <h3>Aplikacja demonstrująca pole wyboru "kilka z wielu" (HOOK)</h3>
            <Form onSubmit={handleSubmit}>
                <Form.Group className="mb-3">
                    <Form.Label>Jakimi środkami transportu podróżujesz na codzień:</Form.Label>
                    <Form.Check
                        type={"checkbox"}
                        label="Autobus"
                        value={"Autobus"}
                        checked={options.member("Autobus")}
                        onChange={handleChange}>
                    </Form.Check>
                    <Form.Check
                        type={"checkbox"}
                        label="Pociag"
                        value={"Pociag"}
                        checked={options.member("Pociag")}
                        onChange={handleChange}>
                    </Form.Check>
                    <Form.Check
                        type={"checkbox"}
                        label="Samochod"
                        value={"Samochod"}
                        checked={options.member("Samochod")}
                        onChange={handleChange}>
                    </Form.Check>
                </Form.Group>
                <Button variant="primary" type="submit">  Potwierdź  </Button>
            </Form>
        </Container>
    );

}
