import React, {useState } from 'react'

import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';

//Demostracja wprowadzanie tekstu wielolinijkowego (HOOK)

export default function TextAreaFormHook() {

  const [myValue, setMyValue] = useState("abc");

  //Obsługa przycisku

  const handleSubmit = (event) => {
    event.preventDefault();
    alert('Podano nastepujacy tekst: ' + myValue);
  }

  return (
    <Container>
      <h3>Aplikacja demonstrująca pole wprowadzanie tekstu wielolinijkowego (HOOK)</h3>

      <Form onSubmit={handleSubmit}>
        <Form.Group className="auto">
          <Form.Label>Podaj tekst:</Form.Label>          
          <Form.Control as="textarea" type="text" value={myValue} placeholder="" onChange={(e) => setMyValue(e.target.value)} />          
        </Form.Group>
        <p></p>
        <Button variant="primary" type="submit">  Potwierdź  </Button>
      </Form >
    </Container>
  );
}
