import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import React, { useEffect, useState } from "react";

export default function GetHiraganaRomajiAndImage() {

    const backendUrl = 'http://localhost:8081';
    const [targetNumberOfLetter, setTargetNumberOfLetter] = useState(1);
    const [status, setStatus] = useState("OK");
    const [image, setImage] = useState("Brak Zdjęcia");
    const [romaji, setRomaji] = useState("Brak Litery");

    async function getRecordById(targetNumberOfLetter) {
        await axios.post(`${backendUrl}/api/v1/gethiraganarecord/`)
            .then(response => {
                let idFound = false; // Dodane, aby śledzić, czy użytkownik został znaleziony
                let hiraganaRomajiName = "";
                let hiraganaImage = "";

                response.data.forEach((e) => {
                    let lid = e.id;
                    let lhiraganaRomaji = e.hiraganaRomaji;
                    let lhiraganaImage = e.hiraganaImage;

                    let firstPartText = lhiraganaRomaji.substring(0, ("ERROR:").length);

                    if (firstPartText !== "ERROR:" && lid === String(targetNumberOfLetter)) {
                        hiraganaRomajiName = `${lhiraganaRomaji}\n`;
                        hiraganaImage = `${lhiraganaImage}\n`;
                        setStatus("OK");
                        idFound = true;
                    } else if (firstPartText === "ERROR:" && lid === targetNumberOfLetter) {
                        let secondPartText = lhiraganaRomaji.substring(("ERROR:").length, lhiraganaRomaji.length);
                        setStatus(secondPartText);
                        idFound = true;
                    }
                });

                if (!idFound) {
                    setStatus("Id nie znaleziono");
                }

                setRomaji(hiraganaRomajiName);
                setImage(hiraganaImage);

            })
            .catch(err => {
                let myerror = "Błąd połączenia sieciowego." + err;
                setStatus(myerror);
            });
    }

    // Wywołanie funkcji z konkretnym username (np. 'john_doe')

    function incrementTarget() {

        targetNumberOfLetter++;
        getRecordById(targetNumberOfLetter);

    }

    useEffect(() => {

        getRecordById(targetNumberOfLetter);

    }, []);

    return (

        <div>

            <button onClick={incrementTarget}>Pobierz Wynik</button>

            <p>{status}</p>
            <p>{romaji}</p>
            <p>{image}</p>

        </div>

    );

}
