import React, { Component, FormEvent, ChangeEvent } from "react";
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Tab from 'react-bootstrap/Tab';
import Tabs from 'react-bootstrap/Tabs';
import 'bootstrap/dist/css/bootstrap.min.css';

//Trzy  komponenty
const Home = () => {
    return <p>To jest komponent główny</p>;
}

const Settings = () => {
    return <p>To są moje opcje</p>;
}

const Topics = () => {
    return <p>To są moje tematy</p>;
}

//-----------------

interface Props {
};

interface State {
    valueFirstName: string;
    valueLastName: string;
}


class TestTabs extends Component<Props, State>
{
    constructor(props: Props) {
        super(props);
        this.state = {
            valueFirstName: "",
            valueLastName: ""
        }

        this.handleChange = this.handleChange.bind(this);        
        this.handleSubmit = this.handleSubmit.bind(this);
    }


  handleChange(evt: ChangeEvent<HTMLInputElement>) {

    const name = evt.target.name;
    if (name=="firstName")
    {
        this.setState({valueFirstName: evt.target.value});      
    }
    else
    {
        this.setState({valueLastName: evt.target.value});      
    }
    
}


handleSubmit(event : FormEvent<HTMLFormElement>) {
    alert('Podano nastepujace imie i nazwisko: ' + this.state.valueFirstName + " " + this.state.valueLastName);
    event.preventDefault();
}

render() {

    return (

        <Container>
        
        <h3>Aplikacja demonstrująca dwa pola wprowadzania tekstu jednolinijkowego</h3>

        <Tabs
      defaultActiveKey="profile"
      id="uncontrolled-tab-example"
      className="mb-3"
    >
      <Tab eventKey="home" title="Główna">
        <Home />
      </Tab>
      <Tab eventKey="profile" title="Opcje">
        <Settings />
      </Tab>
      <Tab eventKey="contact" title="Kontakt">
        <Topics />
      </Tab>
    </Tabs>
        
        
        </Container> 
    );
}
}

export default TestTabs;
