import React, { Component, FormEvent, ChangeEvent, MouseEvent } from "react";
import Container from 'react-bootstrap/Container';
import Modal from 'react-bootstrap/Modal';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Alert from 'react-bootstrap/Alert';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';


//Klient dostępu do przelewów w banku na serwerze z użyciem usług REST z autoryzacja
//Lokalizacja bazy: http://localhost:8081/
//W celu przetestowania klienta należy uruchomić serwer wykonany za pomoca Sprint Boot w języku Java (WEBSERVICE BANK WITH AUTH)


class BankTransBasicAuth extends Component
{

  constructor(props) {
    super(props);

       
    this.state = {
      status: 'OK', textArea: "Brak danych",
      newClientname: "nowakkk", newDate: "2022-10-30", newDescription: "Zakup USD", newAmount: "345.1",  
      transferIDToDelete: "1",      
      userName: "bazan@gmail.com", password: "rzeszow",           
      modalShow: false, modalNewUsername: "nowak", modalNewPassword: "hnowak" 
    };

    //ROLA: ADMIN
    //userName: "admin@gmail.com", password: "polska"         

    //ROLA: CLIENT
    //userName: "nowak@gmail.com", password: "nowaczek"         

    //ROLA: ADMIN i CLIENT
    //userName: "bazan@gmail.com", password: "rzeszow"         

    
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.getTransfers = this.getTransfers.bind(this);
    this.addTransfer = this.addTransfer.bind(this);
    this.deleteTransfer = this.deleteTransfer.bind(this);

    //Dla okna modalnego
    this.handleClose = this.handleClose.bind(this);
    this.handleShow = this.handleShow.bind(this);
    this.handleChangeUser = this.handleChangeUser.bind(this);
    this.handleChangePassword = this.handleChangePassword.bind(this);
    this.handleCloseHeader = this.handleCloseHeader.bind(this);

  }


 //Pobranie danych z magazynu
 getStorage(key) {
  let items = "NULL";
  let res = localStorage.getItem(key);
  if (res == null) items = "NULL";
  else items = res;
  return items;
}

//Wstawienie danych do magazynu
setStorage(key, item) {
  localStorage.setItem(key, item);
}  

    //Dodanie przelewu na serwer
    async addTransfer() {

      let clientname = this.state.newClientname;
      let date = this.state.newDate;
      let description = this.state.newDescription;
      let amount = this.state.newAmount;
      
      
      if ((clientname.length > 0) && (date.length > 0) && (description.length > 0) && (amount.length>0)) {
  
  
        let token = this.state.userName+":"+this.state.password;    
        var etoken = window.btoa(token);      
        var basicAuth = 'Basic ' + etoken;  
  
        await axios.post('http://localhost:8081/padmin/addtransfer/',
          { clientname, date, description, amount }, { headers: { authorization: basicAuth } } //Podajemy parametry
        )
          .then(response => {
            let text = response.data;
            this.setState({ status: text });
          }).catch(err => {
            let myerror = "Błąd połaczenia sieciowego." + err;
            this.setState({ status: myerror });
          });
      }
      else {
        this.setState({ status: "Żadna z danych wstawianego przelewu nie może być pusta" });
      }
    }


  //Pobranie listy przelewow z serwera
  async getTransfers() {

    let token = this.state.userName+":"+this.state.password;    
    var etoken = window.btoa(token);      
    var basicAuth = 'Basic ' + etoken;  
      
        
    await axios.post('http://localhost:8081/pclient/gettransfers/', { }, { headers: { authorization: basicAuth } })        
      .then(response => {
        
        let transferListText = ""

        response.data.forEach((e) => {
            let lid = e.id; 
            let lclientname = e.clientname; let ldate = e.date; let ldescription = e.description; let lamount = e.amount;

          let firstPartText = lclientname.substring(0, ("ERROR:").length);


          if (firstPartText !== "ERROR:") {
            transferListText = transferListText + lid + ", " + lclientname + ", " + ldate + ", " + ldescription + ", " + lamount + "\n";
            this.setState({ textArea: transferListText, status: "OK" });
          }
          else {
            let secondPartText = lclientname.substring(("ERROR:").length, lclientname.length);
            transferListText = lclientname + "\n";
            this.setState({ textArea: transferListText, status: secondPartText });
          }
        })

      }).catch(err => {

        let myerror = "Błąd połaczenia sieciowego." + err;
        this.setState({ status: myerror });
      });

  }
  

  async deleteTransfer() {

    let transferidtodelete = this.state.transferIDToDelete;

    if (transferidtodelete.length > 0) {

      let token = this.state.userName + ":" + this.state.password;
      var etoken = window.btoa(token);
      var basicAuth = 'Basic ' + etoken;

      await axios.post('http://localhost:8081/padmin/deletetransfer/',
        { transferidtodelete }, { headers: { authorization: basicAuth } } //Podajemy parametr
      )
        .then(response => {
          let text = response.data;
          this.setState({ status: text });
        }).catch(err => {
          let myerror = "Błąd połaczenia sieciowego." + err;
          this.setState({ status: myerror });
        });
    }
    else {
      this.setState({ status: "ID przelewu nie może być puste" });
    }
  }
  

  handleChange(evt) {

    const name = evt.target.name;
    
    if (name === "newUsername") this.setState({ newUsername: evt.target.value });
    if (name === "newPassword") this.setState({ newPassword: evt.target.value });
    
    if (name === "newClientname") this.setState({ newClientname: evt.target.value });
    if (name === "newDate") this.setState({ newDate: evt.target.value });
    if (name === "newDescription") this.setState({ newDescription: evt.target.value });
    if (name === "newAmount") this.setState({ newAmount: evt.target.value });
    if (name === "transferIDToDelete") this.setState({ transferIDToDelete: evt.target.value });    
    
  }

  handleSubmit(event) {

    this.addTransfer();
    event.preventDefault(); //Dla zapobiegniecia ponownemu załadowaniu/odświeżeniu przeglądarki
  }


   //--------------------------------------------
   //Obsługa okna wprowadania uzytkownika i hasla

   handleClose(event) {

    let currentButton = event.currentTarget.textContent;

    this.setState({ modalShow: false });

    if (currentButton === "OK") {
      this.setState({ newUsername: this.state.modalNewUsername });
      this.setState({ newPassword: this.state.modalNewPassword });
    }
    else {
      this.setState({ newUsername: this.state.newUsername });
      this.setState({ newPassword: this.state.newPassword });
    }
  }



  handleCloseHeader() {

    this.setState({ modalShow: false });
    this.setState({ newUsername: this.state.newUsername });
    this.setState({ newPassword: this.state.newPassword });
  }


  handleChangeUser(e) {
    this.setState({ modalNewUsername: e.target.value });
  }

  handleChangePassword(e) {
    this.setState({ modalNewPassword: e.target.value });
  }

  handleShow() {    
    this.setState({ modalShow: true });
  }

  //-----------------------------------------------


  render() {
    return (

      <Container>

        <Alert variant="info">
          <Alert.Heading><strong>Klient dostępu do przelewów w banku z użyciem REST (z autoryzacją) </strong></Alert.Heading>
          Lokalizacja bazy: http://localhost:8081/
        </Alert>

        <p></p>

        <h3>Status: {this.state.status}</h3>

        <h4>UserName: {this.state.userName}</h4>

        <h4>Password: {this.state.password}</h4>

        <p></p>

        <Form.Label> Dane nowego przelewu: </Form.Label>

        <Form onSubmit={this.handleSubmit}>
          <Form.Group className="auto" onSubmit={this.handleSubmit}>
            <Form.Label> Klient: <input type="text" name='newClientname' value={this.state.newClientname} onChange={this.handleChange} /> </Form.Label>
            {' '}
            <Form.Label> Data przelewu: <input type="text" name='newDate' value={this.state.newDate} onChange={this.handleChange} /> </Form.Label>
            {' '}
            <Form.Label> Opis przelewu:: <input type="text" name='newDescription' value={this.state.newDescription} onChange={this.handleChange} /> </Form.Label>
            {' '}
            <Form.Label> Kwota: <input type="text" name='newAmount' value={this.state.newAmount} onChange={this.handleChange} /> </Form.Label>
            {' '}
            <Button variant="primary" type="submit">  Dodaj  </Button>
          </Form.Group>
          <p></p>
        </Form >

        <Form.Group className="auto" onSubmit={this.handleSubmit}>
          <Form.Label> Podaj id przelewu do usunięcia: <input type="text" name='transferIDToDelete' value={this.state.transferIDToDelete} onChange={this.handleChange} /> </Form.Label>
          {' '}
          <Button variant="primary" onClick={this.deleteTransfer}>  Usuń  </Button>
        </Form.Group>

        <p></p>
        

        <Button variant="primary" onClick={this.getTransfers}>  Odczytaj aktualną listę przelewów  </Button>

        <p></p>

        <Form.Control as="textarea" rows={8} type="text" value={this.state.textArea} placeholder="" onChange={this.handleChange} spellCheck="false" />

        <p></p>

        <p></p>

        <p>Aktualnie podany użytkonik i hasło: {this.state.newUsername}, {this.state.newPassword}</p>

        <Button variant="primary" onClick={this.handleShow}> Podaj dane do pobrania tokenu </Button>  {' '}

        <Button variant="primary" onClick={this.getToken}> Odczytaj nowy token </Button>

        <p></p>

        <Modal show={this.state.modalShow} onHide={this.handleCloseHeader}>
          <Modal.Header closeButton>
            <Modal.Title>Logowanie</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Form>
              <Form.Group className="auto">
                <Form.Label>Użytkownik:</Form.Label>
                <Form.Control type="text" value={this.state.modalNewUsername} placeholder="" onChange={this.handleChangeUser} />
              </Form.Group>
              <Form.Group className="auto">
                <Form.Label>Hasło:</Form.Label>
                <Form.Control type="text" value={this.state.modalNewPassword} placeholder="" onChange={this.handleChangePassword} />
              </Form.Group>
            </Form >

          </Modal.Body>
          <Modal.Footer>
            <Button variant="primary" onClick={this.handleClose}>
              OK
            </Button>
            <Button variant="secondary" onClick={this.handleClose}>
              Anuluj
            </Button>
          </Modal.Footer>
        </Modal>

      </Container>

    );
  }

}

export default BankTransBasicAuth;
