import React, { Component, FormEvent, ChangeEvent } from "react";
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Alert from 'react-bootstrap/Alert';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';


//Klient dostępu do bazy osób na serwerze z użyciem usług REST
//Lokalizacja bazy: http://localhost:8081/
//W celu przetestowania klienta należy uruchomić serwer wykonany za pomoca Sprint Boot w języku Java



class RESTDataBasePerson extends Component
{

  constructor(props) {
    super(props);

    this.state = {
      status: 'OK', textArea: "Odczytaj osoby",
      newEmail: "ola@gmail.com", newFirstName: "Ola", newLastName: "Kama", newAge: "8",
      emailToRemove: ""
    };

    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.getPersonList = this.getPersonList.bind(this);
    this.deletePerson = this.deletePerson.bind(this);
  }


  async getUsers() {

    await axios.post<TMyUserList>('http://localhost:8081/getusers/')
      .then(response => {

        let personListText = ""

        response.data.forEach((e) => {
          let lemail = e.email; let lfName = e.firstName; let llName = e.lastName; let lage = e.age;
          personListText = personListText + lemail + "," + lfName + " " + llName + "," + lage + "\n";
        })

        this.setState({ textArea: personListText, status: "OK" });

      }).catch(err => {
        let myerror = "Błąd połaczenia sieciowego." + err;        
        this.setState({ status: myerror });
      });
  }


  async addUser() {
    let email = this.state.newEmail;
    let firstName = this.state.newFirstName;
    let lastName = this.state.newLastName;
    let age = this.state.newAge;

    if ((email.length > 0) && (firstName.length > 0) && (lastName.length > 0) && (age.length)) {
      await axios.post('http://localhost:8081/adduser/',
        { email, firstName, lastName, age }, //Podajemy parametry
      )
        .then(response => {
          let text = response.data;
          this.setState({ status: text });
        }).catch(err => {
          let myerror = "Błąd połaczenia sieciowego." + err;
          this.setState({ status: myerror });
        });
    }
    else {
      this.setState({ status: "Żadna z danych wstawianej osoby nie może być pusta" });
    }
  }

  async deleteUser() {
    let emailToRemove = this.state.emailToRemove;

    if (emailToRemove.length > 0) {

      await axios.post('http://localhost:8081/deleteuser/',
        { emailToRemove }, //Podajemy parametr
      )
        .then(response => {
          let text = response.data;
          this.setState({ status: text });
        }).catch(err => {
          let myerror = "Błąd połaczenia sieciowego." + err;
          this.setState({ status: myerror });
        });
    }
    else {
      this.setState({ status: "Adres email nie może być pusty" });
    }
  }


  handleChange(evt) {

    const name = evt.target.name;

    if (name === "newEmail") {
      this.setState({
        newEmail: evt.target.value
      });
    }
    else
      if (name === "newFirstName") {
        this.setState({
          newFirstName: evt.target.value
        });
      }
      else
        if (name === "newLastName") {
          this.setState({
            newLastName: evt.target.value
          });
        }
        else
          if (name === "newAge") {
            this.setState({
              newAge: evt.target.value
            });
          }
          else {
            this.setState({
              emailToRemove: evt.target.value
            });
          }
  }


  handleSubmit(event) {

    let lvalue = this.addUser();
    event.preventDefault();
  }

  getPersonList() {
    this.getUsers();
  }

  deletePerson() {
    this.deleteUser();
  }


  render() {
    return (


      <Container>

        <Alert variant="info">
          <Alert.Heading><strong>Klient dostępu do bazy osób na serwerze z użyciem REST </strong></Alert.Heading>
          Lokalizacja bazy: http://localhost:8081/
        </Alert>


        <p></p>

        <h3>Status: {this.state.status}</h3>

        <p></p>

        <Form.Label> Podaj dane dodawanej osoby: </Form.Label>

        <Form onSubmit={this.handleSubmit}>
          <Form.Group className="auto" onSubmit={this.handleSubmit}>
            <Form.Label> Email: <input type="text" name='newEmail' value={this.state.newEmail} onChange={this.handleChange} /> </Form.Label>
            {' '}
            <Form.Label> Imię: <input type="text" name='newFirstName' value={this.state.newFirstName} onChange={this.handleChange} /> </Form.Label>
            {' '}
            <Form.Label> Nazwisko: <input type="text" name='newLastName' value={this.state.newLastName} onChange={this.handleChange} /> </Form.Label>
            {' '}
            <Form.Label> Wiek: <input type="text" name='newAge' value={this.state.newAge} onChange={this.handleChange} /> </Form.Label>
            {' '}
            <Button variant="primary" type="submit">  Dodaj  </Button>
          </Form.Group>
          <p></p>
        </Form >


        <Form.Group className="auto" onSubmit={this.handleSubmit}>
          <Form.Label> Podaj email osoby do usunięcia: <input type="text" name='deleteID' value={this.state.emailToRemove} onChange={this.handleChange} /> </Form.Label>
          {' '}
          <Button variant="primary" onClick={this.deletePerson}>  Usuń  </Button>
        </Form.Group>

        <p></p>

        <Button variant="primary" onClick={this.getPersonList}> Odczytaj aktualną listę osób </Button>

        <p></p>

        <Form.Control as="textarea" rows={8} cols={60} type="text" value={this.state.textArea} placeholder="" onChange={this.handleChange} spellCheck="false" />

      </Container>


    );
  }
}

export default RESTDataBasePerson;
