import 'bootstrap/dist/css/bootstrap.min.css';
import React, { useState } from "react";
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Modal from 'react-bootstrap/Modal';
import LVariables from "./LVariables";

const [modalShow, setModalShow] = useState(false);

const SomeComponent = () => {
    const { userNameState, passwordState, newUserNameState, newPasswordState } = LVariables();

    const [userName, setUserName] = userNameState;
    const [password, setPassword] = passwordState;
    const [newUserName, setNewUserName] = newUserNameState;
    const [newPassword, setNewPassword] = newPasswordState;

    // Reszta komponentu...
};

export default function Login() {


    // async function handleLogin() {
    //     let token = userName + ":" + password;
    //     var etoken = window.btoa(token);
    //     var basicAuth = 'Basic ' + etoken;

    //     await axios.post('http://localhost:8080/login',
    //         {}, { headers: { authorization: basicAuth } }
    //     )
    //     .then(response => {
    //         // Obsługa sukcesu logowania
    //         console.log("Zalogowano pomyślnie!");
    //     })
    //     .catch(error => {
    //         // Obsługa błędu logowania
    //         console.error("Błąd logowania:", error);
    //     });
    // }

    function handleClose(event) {

        let currentButton = event.currentTarget.textContent;

        setModalShow(false);

        if (currentButton === "OK") {

            LVariables.setUserName(modalNewUsername);
            LVariables.setPassword(modalNewPassword);
        }
        else {
            LVariables.setUserName(userName);
            LVariables.setPassword(password);
        }
    }

    function handleCloseHeader() {

        setModalShow(false);
        LVariables.setUserName(userName);
        LVariables.setPassword(password);
    }

    function handleShow() {
        setModalShow(true);
    }

    return (

        <Container>

            <p>Aktualnie podany użytkownik i hasło: {userName}, {password}</p>

            <Button variant="primary" onClick={handleShow}> Aktualizuj dane do autoryzacji </Button>  {' '}

            <p></p>

            <Modal show={modalShow} onHide={handleCloseHeader}>
                <Modal.Header closeButton>
                    <Modal.Title>Logowanie</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                        <Form.Group className="auto">
                            <Form.Label>Użytkownik:</Form.Label>
                            <Form.Control type="text" value={modalNewUsername} placeholder="" onChange={(e) => setModalNewUsername(e.target.value)} />
                        </Form.Group>
                        <Form.Group className="auto">
                            <Form.Label>Hasło:</Form.Label>
                            <Form.Control type="text" value={modalNewPassword} placeholder="" onChange={(e) => setModalNewPassword(e.target.value)} />
                        </Form.Group>
                    </Form >

                </Modal.Body>
                <Modal.Footer>
                    <Button variant="primary" onClick={handleClose}>
                        OK
                    </Button>
                    <Button variant="secondary" onClick={handleClose}>
                        Anuluj
                    </Button>
                </Modal.Footer>
            </Modal>

        </Container>

    );

}
