import React, { Component, FormEvent, ChangeEvent } from "react";
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Alert from 'react-bootstrap/Alert';

interface Props {    
    callback : (email: string) => void;        
};

interface State {
    email: string;     
    isError: boolean;
    errorText: string;

}


class DeletePerson extends Component<Props, State>
{
    constructor(props: Props) {
        super(props);
        this.state = { email: '', isError: false, errorText: "OK"};
        
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }


    handleChange(e: ChangeEvent<HTMLInputElement>): void {

        this.setState({ email: e.target.value});
    }


    handleSubmit(event: FormEvent<HTMLFormElement>): void {                        
        if (this.state.email.length>0)
        {
            this.props.callback(this.state.email);   
            this.setState({ isError: false, errorText: "OK" });                 
            event.preventDefault();
        }
        else
        {
            this.setState({ isError: true, errorText: "Adres email nie może być pusty." });                             
            event.preventDefault()
        }
    }

    render() {
        return (

            <Container>

                <Alert variant="info">
                    <strong>Podaj email osoby do usuniecia:</strong>
                </Alert>

                <Form onSubmit={this.handleSubmit}>
                    <Form.Group>
                        <Form.Label> Email: <input type="text" name='email' value={this.state.email} onChange={this.handleChange} /> </Form.Label>
                        {this.state.isError && (<div style={{ color: 'red' }}> <Form.Label>{this.state.errorText}</Form.Label> </div>)}                        
                        <p></p>
                        <Button variant="success" type="submit">  Usuń  </Button>
                    </Form.Group>
                </Form>
            </Container>


            
        );
    }
}

export default DeletePerson;
