import React, { useState } from 'react'


export default function ASFormHook() {

    const [text, setText] = useState('');

    //Pobranie wartości z magazynu dla podanego klucza
    function getStorage(key) {
        let items = "NULL";
        let res = localStorage.getItem(key);
        if (res == null) {
            items = "NULL";
        }
        else {
            items = res;
        }
        return items;
    }

    //Wstawienie wartości o podanym kluczu do magazynu
    function setStorage(key, item) {
        localStorage.setItem(key, item);
    }

    //Czyszczenie wartości dla dwóch podanych kluczy
    function bclear() {
        setStorage("ala", "");
        setStorage("ola", "");
    }


    //Wstawienie dwóch wartości, kazde o podanym kluczu 
    function bset() {

        let items1 = getStorage("ala");
        let items2 = getStorage("ola");

        setStorage("ala", items1 + " " + "kowalska");
        setStorage("ola", items2 + " " + "nowak");
    }



    //Pobranie dwóch wartości z magazynu
    function bget() {

        let items1 = getStorage("ala");
        let items2 = getStorage("ola");

        let list = items1 + " " + items2;

        setText(list);

    }


    return (
        <div>
            <h3>Stan: {text}</h3>
            <p></p>
            <button onClick={bclear}>Wyczyść</button>
            <p></p>
            <button onClick={bset}>Dodaj</button>
            <p></p>
            <button onClick={bget}>Pokaż</button>
        </div>
    );

}
