    const name = evt.target.name;

    if (name == "newEmail") {
      this.setState({
        status: this.state.status, textArea: this.state.textArea,
        newEmail: evt.target.value, newFirstName: this.state.newFirstName,
        newLastName: this.state.newLastName, newAge: this.state.newAge, emailToRemove: this.state.emailToRemove
      });
    }
    else
      if (name == "newFirstName") {
        this.setState({
          status: this.state.status, textArea: this.state.textArea,
          newEmail: this.state.newEmail, newFirstName: evt.target.value,
          newLastName: this.state.newLastName, newAge: this.state.newAge, emailToRemove: this.state.emailToRemove
        });
      }
      else
        if (name == "newLastName") {
          this.setState({
            status: this.state.status, textArea: this.state.textArea,
            newEmail: this.state.newEmail, newFirstName: this.state.newFirstName,
            newLastName: evt.target.value, newAge: this.state.newAge, emailToRemove: this.state.emailToRemove
          });
        }
        else
          if (name == "newAge") {
            this.setState({
              status: this.state.status, textArea: this.state.textArea,
              newEmail: this.state.newEmail, newFirstName: this.state.newFirstName,
              newLastName: this.state.newLastName, newAge: evt.target.value, emailToRemove: this.state.emailToRemove
            });
          }
          else {
            this.setState({
              status: this.state.status, textArea: this.state.textArea,
              newEmail: this.state.newEmail, newFirstName: this.state.newFirstName,
              newLastName: this.state.newLastName, newAge: this.state.newAge, emailToRemove: evt.target.value
