  handleSubmit(event: FormEvent<HTMLFormElement>): void {

    let lvalue = this.addUser();
    event.preventDefault();
  }

  getPersonList() {
    this.getUsers();
  }

  deletePerson() {
    this.deleteUser();
  }


  render() {
    return (
      <div>
        <h1>Klient dostępu do bazy osób</h1>

        <h3>Status: {this.state.status}</h3>

        <div><label> Dane osoby do wstawienia:</label></div>
        <form onSubmit={this.handleSubmit}>
          <label> Email: <input type="text" name='newEmail' value={this.state.newEmail} onChange={this.handleChange} />  </label>
          <label> Imie: <input type="text" name='newFirstName' value={this.state.newFirstName} onChange={this.handleChange} />  </label>
          <label> Nazwisko: <input type="text" name='newLastName' value={this.state.newLastName} onChange={this.handleChange} />  </label>
          <label> Age: <input type="text" name='newAge' value={this.state.newAge} onChange={this.handleChange} />  </label>

          <input type="submit" value="Dodaj" />

        </form>

        <div><br></br></div>

        <label> Email osoby do usunięcia: <input type="text" name='deleteID' value={this.state.emailToRemove} onChange={this.handleChange} />  </label>

        <button onClick={this.deletePerson}>Usuń osobę</button>

        <div><br></br></div>
        <div><label> Lista osób w bazie:</label>  <button onClick={this.getPersonList}>Odczytaj aktualną listę osób</button> <br /></div>

        <label> <textarea value={this.state.textArea}
          rows={8} cols={60} />  </label>
      </div>
    );
  }


}

export default DataBasePerson;
