import React, { Component } from "react";
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';

//Aplikacja wprowadzająca dane na dwóch polach (pokaz rozróżniania pola w parciu o atrybut 'name')


class DNameForm extends Component
{
    constructor(props) {
        super(props);
        this.state = {
            valueFirstName: "",
            valueLastName: ""
        }

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }


    handleChange(evt) {

        const name = evt.target.name;
        if (name === "firstName") {
            this.setState({ valueFirstName: evt.target.value });
        }
        else {
            this.setState({ valueLastName: evt.target.value });
        }

    }


    handleSubmit(event) {
        alert('Podano nastepujace imie i nazwisko: ' + this.state.valueFirstName + " " + this.state.valueLastName);
        event.preventDefault();
    }

    render() {

        return (

            <Container>

                <h3>Aplikacja demonstrująca dwa pola wprowadzania tekstu jednolinijkowego</h3>

                <Form onSubmit={this.handleSubmit}>
                    <Form.Group className="auto" onSubmit={this.handleSubmit}>
                        <Form.Label>Podaj imię:</Form.Label>
                        <Form.Control type="text" name='firstName' value={this.state.valueFirstName} placeholder="" onChange={this.handleChange} />
                    </Form.Group>
                    <Form.Group className="auto" onSubmit={this.handleSubmit}>
                        <Form.Label>Podaj nazwisko:</Form.Label>
                        <Form.Control type="text" name='lastName' value={this.state.valueLastName} placeholder="" onChange={this.handleChange} />
                    </Form.Group>
                    <p></p>
                    <Button variant="primary" type="submit">  Potwierdź  </Button>
                </Form >
            </Container>
        );
    }
}

export default DNameForm;
