import React, { Component } from "react";
import Container from 'react-bootstrap/Container';
import Tab from 'react-bootstrap/Tab';
import Tabs from 'react-bootstrap/Tabs';
import 'bootstrap/dist/css/bootstrap.min.css';

//Trzy komponenty funkcyjne, ale jako implementacje zakładek mogą to być komponenty klasowe lub proste teksty
const Main = () => {
    return <p>To jest komponent główny</p>;
}

const Settings = () => {
    return <p>To są moje opcje</p>;
}

const Topics = () => {
    return <p>To są moje tematy</p>;
}

//-----------------

interface Props {
};

interface State {    
}


class TestTabs extends Component<Props, State>
{
    constructor(props: Props) {
        super(props);        
    }

    render() {
        return (
            <Container>
                <h3>Aplikacja demonstrująca menu oparte na zakładkach</h3>
                <Tabs
                    defaultActiveKey="main"
                    id="tab-example"
                    className="mb-3"
                >
                    <Tab eventKey="main" title="Główna">
                        <Main />
                    </Tab>
                    <Tab eventKey="settings" title="Opcje">
                        <Settings />
                    </Tab>
                    <Tab eventKey="topics" title="Tematy">
                        <Topics />
                    </Tab>
                    <Tab eventKey="plaintext" title="Tekst">
                        Warszawa jest stolicą Polski.
                    </Tab>
                </Tabs>
            </Container>
        );
    }
}

export default TestTabs;
