import React, {useState } from 'react'


export default function CounterAppHook() {

  const [counter, setCounter] = useState(0);
  

  //Obsługa przyciskow
  
  const increase = () => {
    setCounter(counter => counter + 1);                
  };

  const decrease1 = () => {
    setCounter(counter => counter - 1);                    
  };

  function decrease() {  
    setCounter(counter => counter - 1);                    
  };
  
  const clear = () => {  
    setCounter(counter => 0);                    
}


  return (
    <div className="counter">
      <h2>Aplikacja demonstrująca obsługę przycisków (JS) - hook </h2>
      <h3>Stan licznika: {counter}</h3>
      <div className="btn__container">
        <button onClick={increase}>Zwiększ o jeden</button>
        <br /><br />
        <button onClick={decrease}>Zmniejsz o jeden</button>
        <br /><br />
        <button onClick={clear}>Zeruj</button>
      </div>
    </div>
  );
}
