import React, {useState } from 'react'
import Table from 'react-bootstrap/Table';
import Container from 'react-bootstrap/Container';
import 'bootstrap/dist/css/bootstrap.min.css';


//Tablica na podstawie listy rekordów (HOOK) 


class Person {
    
    constructor(firstname, lastname, age) {
        this.firstname = firstname;
        this.lastname = lastname;
        this.age = age;
    }
}


export default function Table2Hook() {

    
    let p1 = new Person("Jan", "Kowalski", '35');
    let  p2 = new Person("Marek", "Nowak", '46');
    let p3 = new Person("Anna", "Wazna", '26');
    let pList = [];
    pList.push(p1); pList.push(p2); pList.push(p3);

    const [personList, setPersonList] = useState(pList);
    
    return (

        <Container>

            <h3>Aplikacja demonstrująca tabelę o zmiennej zawartości (HOOK)</h3>

            <Table striped bordered>
                <thead>
                    <tr>
                    <th>Numer</th> <th>Imię</th> <th>Nazwisko</th> <th>Wiek</th>
                    </tr>
                </thead>
                <tbody>
                    {personList.map((lperson, index) => (
                        <tr>
                            <td>{index+1}</td> <td>{lperson.firstname}</td> <td>{lperson.lastname}</td>  <td>{lperson.age}</td>
                        </tr>
                    ))}
                </tbody>
            </Table>

        </Container>
    );
}
