import React, {useState } from 'react'

import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';


export default function CounterAppHook() {

  const [myValue, setMyValue] = useState("abc");

  //Obsługa przyciskow


  const handleSubmit = (event) => {
    event.preventDefault();
    alert('Podano nastepujacy tekst: ' + myValue);
  }

  function handleChange(e) {   
    this.setMyValue(e.target.value);
  }

  //<Form.Control type="text" value={myValue} placeholder="" onChange={(e) => setMyValue(e.target.value)} />

  return (
    <Container>
      <h3>Aplikacja demonstrująca pole wprowadzanie tekstu jednolinijkowego (HOOK)</h3>

      <Form onSubmit={handleSubmit}>
        <Form.Group className="auto">
          <Form.Label>Podaj tekst:</Form.Label>          
          <Form.Control type="text" value={myValue} placeholder="" onChange={handleChange} />
        </Form.Group>
        <p></p>
        <Button variant="primary" type="submit">  Potwierdź  </Button>
      </Form >
    </Container>
  );
}
