import React, { Component } from "react";

//https://react-bootstrap.github.io/


import { Container } from "react-bootstrap";
import Alert from 'react-bootstrap/Alert';

interface Props {
};

interface State {
}


class BootstrapDemo extends Component<Props, State>
{


    constructor(props: Props) {
        super(props);

    }

    //'primary', 'secondary',  'success', 'danger', 'warning', 'info', 'light', 'dark',

    render() {
        return (


            <Container>

                <Alert variant="primary">
                    <Alert.Heading>To jest nagłówek kolorowego obszaru typu primary</Alert.Heading>
                    <p>Można jeszcze używać typy: 'secondary',  'success', 'danger', 'warning', 'info', 'light', 'dark'</p>
                    <p>Znacznik p pozwala uzyskać teksty wielolinijkowe</p>
                </Alert>

                <Alert variant="secondary">
                    To jest demonstracja obszaru typu secondary
                </Alert>

                <Alert variant="success">
                    To jest demonstracja obszaru typu success
                </Alert>

                <Alert variant="danger">
                    To jest demonstracja obszaru typu danger
                </Alert>

                <Alert variant="warning">
                    To jest demonstracja obszaru typu warning
                </Alert>

                <Alert variant="info">
                    To jest demonstracja obszaru typu info
                </Alert>

                <Alert variant="light">
                    To jest demonstracja obszaru typu light
                </Alert>

                <Alert variant="dark">
                    To jest demonstracja obszaru typu dark
                </Alert>



                <Alert variant="primary">
                    <strong>To jest demonstracja użycia linków w komponencie do innych stron:</strong>
                    <Alert.Link href="https://www.google.com/">Link do strony www.google.com</Alert.Link> <p></p>
                </Alert>


            </Container>

        );
    }



}

export default BootstrapDemo;
