import React, { Component, FormEvent, ChangeEvent } from "react";
import axios from 'axios';

//Bez parametrow zewnetrznych
interface Props {
};

//Parametry stanu komponentu
interface State {
  status: string;
  textArea: string;

  //Do przelewu
  newClientname: string;
  newDate: string;
  newRecipient: string;
  newAmount: string;

  //Do logowania
  newUsername: string;
  newPassword: string;

}

//Pomocniczy interfejs do przesylania danych od serwera
interface Transfer {
  clientname: string;
  date: string;
  recipient: string;
  amount: number;
};



export type TMyTransferList = Transfer[] //Definiujemy pomocniczy typ

class BankTrans extends Component<Props, State>
{


  constructor(props: Props) {
    super(props);

    this.state = {
      status: 'OK', textArea: "Odczytaj przelewy",
      newClientname: "nowak", newDate: "2022-10-30", newRecipient: "Paribas", newAmount: "23.2",
      newUsername: "nowak", newPassword: "hnowak",
    };

    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.getTransfers = this.getTransfers.bind(this);
    this.getToken = this.getToken.bind(this);
  }

  //Pobranie danych z magazynu
  getStorage(key: string): string {
    let items: string = "NULL";
    let res = localStorage.getItem(key);
    if (res == null) items = "NULL";
    else items = res;
    return items;
  }

  //Wstawienie danych do magazynu
  setStorage(key: string, item: string): void {
    localStorage.setItem(key, item);
  }

  async getToken() {

    let username = this.state.newUsername;
    let password = this.state.newPassword;

    if ((username.length > 0) && (password.length > 0)) {

      await axios.post<string>('http://localhost:8081/login/',
        { username, password }, //Podajemy parametry
      )
        .then(response => {

          let firstPartText = response.data.substring(0, ("ERROR:").length);
          let secondPartText = response.data.substring(("ERROR:").length, response.data.length);

          if (firstPartText === "ERROR:") {
            this.setState({ status: secondPartText });
          }
          else {
            this.setStorage("TOKEN", response.data);
            this.setState({ status: "Pobrano nowy token." });
          }

        }).catch(err => {
          let myerror = "Błąd połaczenia sieciowego." + err;
          this.setState({ status: myerror });
        });
    }

  }

  async getTransfers() {


    let token = "Bearer " + this.getStorage("TOKEN"); //Pobranie tokenu z magazynu

    await axios.get<TMyTransferList>('http://localhost:8081/jwt/gettransfers/', { headers: { 'Authorization': token } })
      .then(response => {


        let transferListText = ""

        response.data.forEach((e) => {
          let lclientname = e.clientname; let ldate = e.date; let lrecipient = e.recipient; let lamount = e.amount;

          let firstPartText = lclientname.substring(0, ("ERROR:").length);
          

          if (firstPartText !== "ERROR:") {
            transferListText = transferListText + lclientname + "," + ldate + " " + lrecipient + "," + lamount + "\n";
            this.setState({ textArea: transferListText, status: "OK" });
          }
          else {
            let secondPartText = lclientname.substring(("ERROR:").length, lclientname.length);
            transferListText = lclientname + "\n";
            this.setState({ textArea: transferListText, status: secondPartText });
          }
        })

        

      }).catch(err => {
        let myerror = "Błąd połaczenia sieciowego." + err;
        this.setState({ status: myerror });
      });

  }


  async addTransfer() {
    let clientname = this.state.newClientname;
    let date = this.state.newDate;
    let recipient = this.state.newRecipient;
    let amount = this.state.newAmount;

    let token = "Bearer " + this.getStorage("TOKEN"); //Pobranie tokenu z magazynu


    if ((clientname.length > 0) && (date.length > 0) && (recipient.length > 0) && (parseInt(amount) > 0)) {

      let jsonStringVal = JSON.stringify({ clientname: clientname, date: date, recipient: recipient, amount: amount });

      await axios.get<string>('http://localhost:8081/jwt/addtransfer/', { headers: { 'Authorization': token }, params: { jsonString: jsonStringVal } })
        .then(response => {

          let firstPartText = response.data.substring(0, ("ERROR:").length);
          let secondPartText = response.data.substring(("ERROR:").length, response.data.length);

          if (firstPartText === "ERROR:") {
            this.setState({ status: secondPartText });
          }
          else {
            this.setState({ status: response.data });
          }

        }).catch(err => {
          let myerror = "Błąd połaczenia sieciowego." + err;
          this.setState({ status: myerror });
        });
    }
    else {
      this.setState({ status: "Żadna z danych wstawianej osoby nie może być pusta" });
    }
  }


  handleChange(evt: ChangeEvent<HTMLInputElement>) {

    const name = evt.target.name;

    if (name === "newUsername") this.setState({ newUsername: evt.target.value });
    if (name === "newPassword") this.setState({ newPassword: evt.target.value });

    if (name === "newClientname") this.setState({ newClientname: evt.target.value });
    if (name === "newDate") this.setState({ newDate: evt.target.value });
    if (name === "newRecipient") this.setState({ newRecipient: evt.target.value });
    if (name === "newAmount") this.setState({ newAmount: evt.target.value });   
  }

  handleSubmit(event: FormEvent<HTMLFormElement>): void {

    this.addTransfer();
    event.preventDefault(); //Dla zapobiegnieciu ponownemu załadowaniu/odświeżeniu przeglądarki
  }


  render() {
    return (
      <div>
        <h1>Klient dostępu do przelewów w banku</h1>

        <h3>Status: {this.state.status}</h3>

        <div><label> Dane nowego przelewu:</label></div>
        <form onSubmit={this.handleSubmit}>
          <label> Klient: <input type="text" name='newClientname' value={this.state.newClientname} onChange={this.handleChange} />  </label>
          <label> Data przelewu: <input type="text" name='newDate' value={this.state.newDate} onChange={this.handleChange} />  </label>
          <label> Odbiorca przelewu: <input type="text" name='newRecipient' value={this.state.newRecipient} onChange={this.handleChange} />  </label>
          <label> Kwota: <input type="text" name='newAmount' value={this.state.newAmount} onChange={this.handleChange} />  </label>

          <input type="submit" value="Dodaj" />

        </form>


        <div><br></br></div>
        <div><label> Lista przelewów w bazie:</label>  <button onClick={this.getTransfers}>Odczytaj aktualną listę przelewów</button> <br /></div>
                
        <label> <textarea value={this.state.textArea} rows={8} cols={60} Read-only spellCheck="false" />  </label>

        <div><br></br></div>
        <div><label> Odczytanie tokenu (logowanie):</label>
        <div></div>
        <label> Użytkownik: <input type="text" name='newUsername' value={this.state.newUsername} onChange={this.handleChange} />  </label>
        <label> Hasło: <input type="text" name='newPassword' value={this.state.newPassword} onChange={this.handleChange} />  </label>

        <button onClick={this.getToken}>Odczytaj nowy token</button> <br /></div>
      </div>
    );
  }


}

export default BankTrans;
