import React, {useState } from 'react'

import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';

//Aplikacja wprowadzająca dane na dwóch polach (pokaz rozróżniania pola w parciu o atrybut 'name')

export default function DNameFormHook() {

  const [state, setState] = useState({valueFirstName: "Jan",valueLastName: "Kowal"});


  //Obsługa przycisku

  const handleSubmit = (event) => {
    event.preventDefault();
    alert('Podano nastepujacy tekst: ' + myValue);
  }

  //Obsluga aktualizacji
  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setState((prevProps) => ({
      ...prevProps,
      [name]: value
    }));
  };


  return (
    <Container>
        <h3>Aplikacja demonstrująca dwa pola wprowadzania tekstu jednolinijkowego</h3>

        <Form onSubmit={this.handleSubmit}>
            <Form.Group className="auto">
                <Form.Label>Podaj imię:</Form.Label>
                <Form.Control type="text" name='firstName' value={this.state.valueFirstName} placeholder="" onChange={handleInputChange} />
            </Form.Group>
            <Form.Group className="auto" onSubmit={this.handleSubmit}>
                <Form.Label>Podaj nazwisko:</Form.Label>
                <Form.Control type="text" name='lastName' value={this.state.valueLastName} placeholder="" onChange={handleInputChange} />
            </Form.Group>
            <p></p>
            <Button variant="primary" type="submit">  Potwierdź  </Button>
        </Form >
    </Container>
);



  return (
    <Container>
      <h3>Aplikacja demonstrująca pole wprowadzanie tekstu jednolinijkowego (HOOK)</h3>

      <Form onSubmit={handleSubmit}>
        <Form.Group className="auto">
          <Form.Label>Podaj tekst:</Form.Label>          
          <Form.Control type="text" value={myValue} placeholder="" onChange={(e) => setMyValue(e.target.value)} />          
        </Form.Group>
        <p></p>
        <Button variant="primary" type="submit">  Potwierdź  </Button>
      </Form >
    </Container>
  );
}
