import React, { Component } from "react";


interface Props {   
};

interface State {    
    counter : number;    
  }

class CounterApp extends Component<Props,State> {
    constructor(props : Props) {
        super(props);
        
        this.state = {
            counter: 0
        }
       

        this.increase = this.increase.bind(this);
        this.decrease = this.decrease.bind(this);
        this.clear = this.clear.bind(this);
    }
    increase() {
        this.setState((prevState : State) => ({
            counter: prevState.counter + 1
        }));
    }
    decrease() {
        this.setState((prevState : State) => ({
            counter: prevState.counter - 1
        }));
    }
    clear() {
        this.setState({
            counter: 0
        });
    }
    render() {
        return (
            <div>

                <h3>Stan licznika: {this.state.counter}</h3>                    
                <button onClick={this.increase}>ZwiÄ™ksz o jeden</button>
                <br /><br />
                <button onClick={this.decrease}>Zmniejsz o jeden</button>
                <br /><br />
                <button onClick={this.clear}>Zeruj</button>
            </div>
        );
    }
}
export default CounterApp;
