import React, { Component, FormEvent, ChangeEvent } from "react";

//https://react-bootstrap.github.io/

import { Row, Col, Container } from "react-bootstrap";
import Button from 'react-bootstrap/Button';
import Alert from 'react-bootstrap/Alert';
import Table from 'react-bootstrap/Table';
import Form from 'react-bootstrap/Form';
import Dropdown from 'react-bootstrap/Dropdown';
import Tab from 'react-bootstrap/Tab';
import Tabs from 'react-bootstrap/Tabs';
import Stack from 'react-bootstrap/Stack';

//import Jumbotron from 'react-bootstrap/Jumbotron';

import {
    BrowserRouter as Router,
    Link,
    NavLink,
    Route
} from "react-router-dom";

import { Navbar, Nav } from "react-bootstrap";


import CounterApp from '../counter/CounterApp';

import 'bootstrap/dist/css/bootstrap.min.css';

import Other from './Other';
import Other2 from './Other2';

import FirstApp from '../firstapp/FirstApp';

class BDPerson {

    public email: string;
    public firstname: string;
    public lastname: string;
    public age: string;

    constructor(email: string, firstname: string, lastname: string, age: string) {
        this.email = email;
        this.firstname = firstname;
        this.lastname = lastname;
        this.age = age;
    }
}

interface Props {
};

interface State {
    status: string;
    textArea: string;

    newEmail: string;
    newFirstName: string;
    newLastName: string;
    newAge: string;

    emailToRemove: string;

    personList: BDPerson[];
    alertType: string;
}


class BootstrapDemo extends Component<Props, State>
{


    constructor(props: Props) {
        super(props);

        this.state = {
            status: 'OK', textArea: "",
            newEmail: "ola@gmail.com", newFirstName: "Ola", newLastName: "Kama", newAge: "8",
            emailToRemove: "",
            personList: [],
            alertType: 'primary'
        };

        let p1 = new BDPerson("jkowalski@gmail.com", "Jan", "Kowalski", '35');
        let p2 = new BDPerson("mnowak@gmail.com", "Marek", "Nowak", '46');
        let p3 = new BDPerson("awazna@gmail.com", "Anna", "Wazna", '26');

        this.state.personList.push(p1); this.state.personList.push(p2); this.state.personList.push(p3);


        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.deletePerson = this.deletePerson.bind(this);
    }


    /*
        addUser() {
            let email = this.state.newEmail;
            let firstName = this.state.newFirstName;
            let lastName = this.state.newLastName;
            let age = this.state.newAge;
    
            if ((email.length > 0) && (firstName.length > 0) && (lastName.length > 0) && (age.length)) {
    
                let p: BPerson = new BPerson(email, firstName, lastName, age);
    
                this.state.personList.add(p);
    
                this.setState((prevState) => ({
                    personList: prevState.personList
                }));
            }
            else {
                this.setState({ status: "Żadna z danych wstawianej osoby nie może być pusta" });
            }
        }
        */

    deleteUser() {
        let emailToRemove = this.state.emailToRemove;

        if (emailToRemove.length > 0) {

            for (let i: number = 0; i < this.state.personList.length; i = i + 1) {
                let person = this.state.personList[i];
                if (person.email === emailToRemove) this.state.personList.splice(i, 1);
            }

            this.setState((prevState) => ({
                personList: prevState.personList
            }));
        }
        else {
            this.setState({ status: "Adres email nie może być pusty" });
        }
    }


    handleChange(evt: ChangeEvent<HTMLInputElement>) {

        const name = evt.target.name;


        if (name === "newEmail") {
            this.setState({
                newEmail: evt.target.value
            });
        }
        else
            if (name === "newFirstName") {
                this.setState({
                    newFirstName: evt.target.value
                });
            }
            else
                if (name === "newLastName") {
                    this.setState({
                        newLastName: evt.target.value
                    });
                }
                else
                    if (name === "newAge") {
                        this.setState({
                            newAge: evt.target.value
                        });
                    }
                    else {
                        this.setState({
                            emailToRemove: evt.target.value
                        });
                    }

    }


    handleSubmit(event: FormEvent<HTMLFormElement>): void {

        //this.addUser();
        event.preventDefault();
    }


    deletePerson() {
        this.deleteUser();
    }

    opcja() {
        alert("aaaaaaa");
    }

    //'primary', 'secondary',  'success', 'danger', 'warning', 'info', 'light', 'dark',

    render() {
        return (


            <Container>

                <Nav
                    activeKey="/home"
                    onSelect={(selectedKey) => alert(`selected ${selectedKey}`)}
                >
                    <Nav.Item>
                        <Nav.Link href="/home">Active</Nav.Link>
                    </Nav.Item>
                    <Nav.Item>
                        <Nav.Link eventKey="link-1"><FirstApp /></Nav.Link>
                    </Nav.Item>
                    <Nav.Item>
                        <Nav.Link eventKey="link-2">Link</Nav.Link>
                    </Nav.Item>
                    <Nav.Item>
                        <Nav.Link eventKey="disabled" disabled>
                            Disabled
                        </Nav.Link>
                    </Nav.Item>
                </Nav>

                <Alert variant="primary">
                    <Alert.Heading>To jest nagłówek kolorowego obszaru typu primary</Alert.Heading>
                    <p>Można jeszcze używać typy: 'secondary',  'success', 'danger', 'warning', 'info', 'light', 'dark'</p>
                    <p>Znacznik p pozwala uzyskać teksty wielolinijkowe</p>
                </Alert>

                <Alert variant="primary">
                    <h3><strong>To jest demonstracja użycia linków w komponencie do innych stron:</strong></h3> <p></p>
                    <Alert.Link href="https://www.google.com/">Link do strony www.google.com</Alert.Link> <p></p>
                </Alert>


                <Stack gap={3} className="col-md-5 mx-auto">
                    <Button variant="secondary">Save changes</Button>
                    <Button variant="outline-secondary1">Cancel</Button>
                    <Button variant="outline-secondary2">Cancel</Button>
                    <Button variant="outline-secondary3">Cancel</Button>
                </Stack>

                <Stack direction="horizontal" gap={3}>
                    <div className="bg-light border">First item</div>
                    <div className="bg-light border">Second item</div>
                    <div className="bg-light border">Third item</div>
                </Stack>


                <h1>Klient dostępu do bazy osób w pamięci RAM</h1>

                <Alert variant={this.state.alertType}>
                    Status: {this.state.status}
                </Alert>


                <form>
                    <Row>
                        <Col xs={12}>
                            <h1>Podaj nową osobę:</h1>
                        </Col>
                    </Row>
                    <Row>
                        <Col xs={12} md={4}>
                            <p>Email:</p>
                            <input type="text" name='newEmail' value={this.state.newEmail} onChange={this.handleChange} />
                        </Col>
                        <Col xs={12} md={4}>
                            <p>Nazwisko:</p>
                            <input type="text" name="lastname" />
                        </Col>
                    </Row>
                    <Row>
                        <Col xs={12}>
                            <input type="submit" value="Dodaj" />
                        </Col>
                    </Row>
                </form>


                <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label>Email osoby do usunięcia: </Form.Label>
                    <Form.Control type="text" placeholder={this.state.emailToRemove} onChange={this.handleChange} />
                    <Button variant="primary" onClick={this.deletePerson}> Usuń osobę </Button>
                </Form.Group>


                <Alert variant="primary">
                    <strong>Lista osób</strong>
                </Alert>


                <Table striped bordered>
                    <thead>
                        <tr>
                            <th>Imię</th> <th>Nazwisko</th> <th>Email</th> <th>Wiek</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.state.personList.map((lperson, index) => (
                            <tr>
                                <td>{lperson.firstname}</td> <td>{lperson.lastname}</td> <td>{lperson.email}</td>  <td>{lperson.age}</td>
                            </tr>
                        ))}
                    </tbody>
                </Table>



                <Dropdown>
                    <Dropdown.Toggle variant="success" id="dropdown-basic">
                        Dropdown Button
                    </Dropdown.Toggle>

                    <Dropdown.Menu>
                        <Dropdown.Item tag="a" href="./other.html">YourLink</Dropdown.Item>
                        <Dropdown.Item href="other.html">Action</Dropdown.Item>
                        <Dropdown.Item href="<Other2 />">AAnother action</Dropdown.Item>
                        <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
                    </Dropdown.Menu>
                </Dropdown>

                <Tabs
                    defaultActiveKey="profile"
                    id="uncontrolled-tab-example"
                    className="mb-3"
                >
                    <Tab eventKey="home" title="Home">
                        <CounterApp />
                    </Tab>
                    <Tab eventKey="profile" title="Profile">
                        <Other2 />
                    </Tab>
                    <Tab eventKey="contact" title="Contact" disabled>
                        <CounterApp />
                    </Tab>
                </Tabs>


                <form onSubmit={this.handleSubmit}>
                    <Form.Group controlId="kindOfStand">
                        <Form.Check
                            value="design"
                            type="radio"
                            aria-label="radio 1"
                            label="Design"
                            onChange={this.handleChange}
                            checked={true}
                        />
                        <Form.Check
                            value="food"
                            type="radio"
                            aria-label="radio 2"
                            label="Food"
                            onChange={this.handleChange}
                            checked={false}
                        />
                    </Form.Group>
                    <Button variant="primary" type="submit">
                        Submit
                    </Button>
                </form>

                <p></p>

                <div onChange={this.onChangeValue1}>
                    <input type="radio" value="Male" name="gender" /> Male
                    <input type="radio" value="Female" name="gender" /> Female
                    <input type="radio" value="Other" name="gender" /> Other
                </div>

                <p></p>

                <Form>
                    <div onChange={this.onChangeValue1}>
                        <Form.Check type="radio" label="Male" />
                        <Form.Check type="radio" label="Fenale" />
                        <Form.Check type="radio" label="Other" />
                    </div>
                </Form>

            </Container>

        );
    }

    onChangeValue1(evt: ChangeEvent<HTMLInputElement>) {
        alert(evt.target.value);
    }


}

export default BootstrapDemo;
