import React, { Component, FormEvent, ChangeEvent } from "react";
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';

import 'bootstrap/dist/css/bootstrap.min.css';


class ChatConnectionData
{
    private textOne : string;
    private textTwo : string;

    constructor(textOne : string,textTwo: string) {  
        this.textOne = textOne;
        this.textTwo = textTwo;
    }

    setOne(text : string) : void{
        this.textOne = text;        
    }

    setTwo(text : string) : void{
        this.textTwo = text;        
    }

    getOne() : string{
        return this.textOne;
    }

    getTwo() : string{
        return this.textTwo;
    }


}

//-------------------------

interface UProps {
    connection : ChatConnectionData;    
    id : string
};

interface UState {  
    value : string;
}


class UserForm extends Component<UProps, UState>
{
  constructor(props: UProps) {
    super(props);
    this.state = { value: props.connection.getOne() };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

  }

  handleChange(e: ChangeEvent<HTMLInputElement>): void {
    //alert('Zmiana: ' + e.target.value);   
    this.setState({ value: e.target.value });
  }


  handleSubmit(event: FormEvent<HTMLFormElement>): void {    
    this.props.connection.setOne("Ala");    
    event.preventDefault();
  }

  render() {
    return (

      <Container>

        
        <h3>Telefonwania: {this.props.connection.getOne()} </h3>

        <Form onSubmit={this.handleSubmit}>
          <Form.Group className="auto" onSubmit={this.handleSubmit}>
            <Form.Label>Podaj tekst:</Form.Label>
            <Form.Control type="text" value={this.state.value} placeholder="" onChange={this.handleChange} />
          </Form.Group>
          <p></p>
          <Button variant="primary" type="submit">  Potwierdź  </Button>
        </Form >            

      </Container>

    );
  }
}


//==================================

interface Props {   
};

interface State {    
    connection : ChatConnectionData;
}


// <UserForm connection={this.state.connection} id={"one"} />
//<UserForm connection={this.state.connection} id={"two"} />
                

class PChat extends Component<Props, State>
{
    constructor(props: Props) {
        super(props);
        this.state = { connection : new ChatConnectionData("ala","ola") };
     
        //this.handleShow = this.handleShow.bind(this);


    }

   
    render() {
        return (
            <div>

                <h3>Telefon pomiędzy komponentami </h3>
                                
                <UserForm connection={this.state.connection} id={"one"} />
               
                <UserForm connection={this.state.connection} id={"two"} />
                
            </div>
        );
    }
}


export default PChat;
