import React, {useState } from 'react'

import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';

//Aplikacja wprowadzająca dane na dwóch polach (pokaz rozróżniania pola w parciu o atrybut 'name')

export default function DNameFormHook() {

  const [state, setState] = useState({ valueFirstName: "Jan", valueLastName: "Kowal" });


  //Obsługa przycisku
  const handleSubmit = (event) => {
    event.preventDefault();
    alert('Podano nastepujace teksty: ' + state.valueFirstName+ " "+ state.valueLastName);
  }

  //Obsluga aktualizacji stanu
  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setState((prevProps) => ({
      ...prevProps,
      [name]: value
    }));
  };


  return (
    <Container>
      <h3>Aplikacja demonstrująca dwa pola wprowadzania tekstu jednolinijkowego</h3>

      <Form onSubmit={this.handleSubmit}>
        <Form.Group className="auto">
          <Form.Label>Podaj imię:</Form.Label>
          
        </Form.Group>
        <Form.Group className="auto" onSubmit={this.handleSubmit}>
          <Form.Label>Podaj nazwisko:</Form.Label>
          
        </Form.Group>
        <p></p>
        <Button variant="primary" type="submit">  Potwierdź  </Button>
      </Form >
    </Container>
  );

}
