import  SBookHook from "./SBookHook"

//Kolekcja książek
class SBookListHook{
    
    constructor(){
        this.books = [];        
    }

    add(book) {
        this.books.push(book);
    }

    get(index) {
        return this.books[index];
    }

    size() {
        return this.books.length;
    }

    remove(index) {

        if ((index >=0)&&(index<this.books.length)) {
            this.books.splice(index, 1);
        }
    }

    getCopy() {

        let locBookList = new SBookListHook();

        if (size()>0)
        {
            for (let i=0; i<this.size(); i=i+1)
            {
                let book = this.get(i);
                locBookList.add(book);                
            }            
        }

        return locBookList;
    }

    //Utworzenie bazy poczatkowej ksiazek
    build() {
        let b1 = new SBookHook("Hobbittt","Tolkien","455");
        let b2 = new SBookHook("Rok 1984","Orwell","455");
        let b3 = new SBookHook("Władca Pierścieni","Tolkien","669");
        let b4 = new SBookHook("Stary człowiek i morze","Hemingway","226");

        this.add(b1);this.add(b2);this.add(b3);this.add(b4);
    }

    //Selekcja książek o ustalonym kryterium wyboru
    selectBooks() {

        if (this.size()>0)
        {

            let nbooks = [];  

            for (let i=0; i<this.size(); i=i+1)
            {
                let book = this.get(i);
                let author = book.author;
                
                if (author=="Tolkien"){
                    nbooks.push(book);        
                }
            }            

            this.books = nbooks;

        }
        
    }

    //Filtracja książek z dynamicznie podanym tekstem, który szukamy opisie
    filtrBooks(filtrText) {
        if (this.size()>0)
        {

            let filter = filtrText.toUpperCase();

            let nbooks  = [];  

            for (let i=0; i<this.size(); i=i+1)
            {
                let book = this.get(i);
                let author = book.author;
                let title = book.title;
                let text = author + " " + title;
                text = text.toUpperCase();

                if (text.includes(filter))
                {
                    nbooks.push(book);        
                }
            }            

            this.books = nbooks;
        }
        
    }

    //Dodanie ustalonej ksiazki
    addBook() {

        let b = new SBookHook("Pan Tadeusz","Mickiewicz","345");        
        this.add(b);
    }

    
}

export default SBookListHook;
