import React, {useState } from 'react'

import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';


export default function DNameFormHook() {

  const [state, setState] = useState({ valueFirstName: "Jan", valueLastName: "Kowal" });


  //Obsługa przycisku
  const handleSubmit = (event) => {
    event.preventDefault();
    alert('Podano nastepujace teksty: ' + state.valueFirstName + " " + state.valueLastName);
  }

  //Obsluga aktualizacji stanu
  const handleChange = (event) => {
    const { name, value } = event.target;
    
    if (name == "firstName") {      
      setState({ valueFirstName: value });
    }
    else {
      setState({ valueLastName: value });
    }
  };


/*
  return (
    <Container>
      <h3>Aplikacja demonstrująca pole wprowadzanie tekstu jednolinijkowego (HOOK)</h3>

      <Form onSubmit={handleSubmit}>
        <Form.Group className="auto">
          <Form.Label>Podaj tekst:</Form.Label>          
          <Form.Control type="text" value={state.valueFirstName} placeholder=""  />          
        </Form.Group>
        <p></p>
        <Button variant="primary" type="submit">  Potwierdź  </Button>
      </Form >
    </Container>
  );
}
*/

  
  return (

    <Container>

      <h3>Aplikacja demonstrująca dwa pola wprowadzania tekstu jednolinijkowego (HOOK)</h3>

      <Form onSubmit={handleSubmit}>
        <Form.Group className="auto">
          <Form.Label>Podaj imię:</Form.Label>
          <Form.Control type="text" name='firstName' value={state.valueFirstName} placeholder="" onChange={handleChange} />          
        </Form.Group>
        <Form.Group className="auto" >
          <Form.Label>Podaj nazwisko:</Form.Label>
          <Form.Control type="text" name='lastName' value={state.valueLastName} placeholder=""  onChange={handleChange} />
        </Form.Group>
        <p></p>
        <Button variant="primary" type="submit">  Potwierdź  </Button>
      </Form >
    </Container>
  );

}
